css:
/* FF.net blocks selection in the #storytextp element
 *  in the ham2() function in combo.js,
 *  after a 200-millisecond delay set by the ham() function */
/* this user style is an accidental, slightly-better dupe of
 *  http://userstyles.org/styles/93798/fanfiction-net-make-text-selectable-again */

@-moz-document domain("fanfiction.net") {
    #storytextp {
        -webkit-touch-callout: text !important;
        -webkit-user-select: text !important;
        -khtml-user-select: text !important;
        -moz-user-select: text !important;
        -ms-user-select: text !important;
        user-select: text !important; 
    }
}
