css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("derpydeliverence.webs.com") {
html {
 background: none #999 !important;
}
#logo-div {
 background: url("data:image/png;base64,") !important;
}
#fw-mainnavwrap {
 margin: 0 !important;
 padding: 6px !important;
 border-width: 0 !important;
 text-align: center !important;
}
#fw-mainnavwrap ul li {
 margin: 0 !important;
}
#fw-mainnavwrap ul li a {
 padding: 6px !important;
}
#fw-sidebar {
 height: 0px !important;
 overflow: hidden !important;
}
#fw-bigcontain {
 width: 100% !important;
}
table#searchDisplay, table#footer {
 border-width: 0 !important;
}
.fw-searchContainer {
 display: none !important;
}
ul#welcome-module {
 visibility: visible;
 position: absolute;
 top: 40px;
 right: 20px;
 padding: 7px !important;
 border: 1px solid #000;
 background: #333;
}
#fw-footer {
 padding: 0 !important;
 background: none !important;
}
}
