@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://krautchan.net/int/"), url("http://krautchan.net/"), url-prefix('http://daten.dieweltistgarnichtso.net/src/blinkenworld/') {

/* 
clean up
*/
ul[style="text-align: left; margin: 15px"],
hr,
div.adminbar,
.loading-visible,
table.pages input,
td.postblock,
td.rules span,
div span[style="float:right"],
div.thread span[style="float:right"],
div.postarea hr,
body center spam,

div.replymode,
img.likebutton,
img.tweetbutton,
div.footer,
.logo+hr,
.logo+hr+div+hr,
hr+script+center>a,
hr+script+center+hr,
#recaptcha_logo,
#recaptcha_tagline,
table+br+hr+center>a,
table+br+hr+center+hr,
.postarea>form>div[style]>a[target],
.doubledash,
td[valign="top"],
DIV.postform_info_notice,
form[action="/paint"],
script[type="text/javascript"],
div[style="float: right"],
center h2 {
display: none !important;
}




* {
    border: none !important;
    background: none !important;
    font-size: 12px !important;
    text-decoration: none !important;
    font-weight: normal !important;
}

div[id="WzBoDy"] {
    background: #fff !important;
    border: 1px solid #aaaacc !important;
    }
div[id="WzTiTl"] {
    background: #aaaacc !important;
    }
    
    

    

/*postform*/

table.postform {
    position: fixed;
    width: 1000px !important;
    right: -743px; top: 110px;
    border-radius: 0px;
}

TD#postform_label_subject, TD#postform_label_comment, TD#postform_label_files, .label  {
display: none !important;
}
TD#postform_label_sage {
display: inline !important;
position: fixed;
width: 20px !important;
margin-top: 1px !important;
right: 215px !important;
top: 89px !important;
}

input[tabindex="4"] {
right: 238px !important;
top: 90px !important;
position: fixed;
}

input[tabindex="7"] {
background: rgba(0,0,0,0) !important
}

table.postform textarea {

    width: 250px;
    height: 100px;
    -webkit-appearance: none;
    -moz-appearance: none;
    margin-left: 0px;
    margin-top: -6px !important
}
table.postform textarea:focus {
    position: fixed;
    height: 230px;
    min-width: 700px;
    right: -2px;
    background-color: #fff !important;
    opacity: 1 !important;
    z-index: 99;
    -moz-transition: all 0.3s ease;
}

input[tabindex="3"]{
    width: 188px !important;
    height:17px !important;
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    }
input[tabindex="10"] {
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    height:17px !important;
    margin-top: -6px !important
    }
input[name="captcha_secret"] {
    position: fixed;
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    height:30px !important;
    width:100px !important;
    top: 240px !important;
    }
#captcha_image {
    margin-top: -8px !important;
    }

#postform_submit{
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    }
    
/*Boards*/
div[style="float: left" ]{
    position: absolute !important;
    left: 0px !important;
    top: -1px !important;
    right: 255px !important;
    padding: 0px !important;
    text-align: center;
}

/*Logo & Descrição*/
img[src="/banner/int"] {
    position: fixed !important;
    margin: -1px !important;
    max-width: 253px;
    height: auto !important;
    top: 2px !important;
    right: 2px !important;
    }
center h1 {
    width: 253px!important;
    position: fixed;
    top: 60px; right: 1px;
    color: #fff !important;
    background: rgba(0,0,0,.6) !important;
    border-radius: 0px;

}
a[href="http://minecraft.krautchan.net/doku.php"], a[href="irc://irc.inn.at.euirc.net/%23%2fint%2f"], center p {
display: none !important;
}

/*Sizing*/

span.extrabtns img {
    max-height: 11px;
    background-size: 11px !important;
    border-radius: 1px;
}
form div table, form table {
    width: 100%
}

/*Não sei o motivo, mas sem esse padding alguns posts ficam desalinhados*/
form div div {
padding: 1px;
}

body, form {
    margin-right: 122px !important;
    margin-left: 2px !important;
    margin-top: 20px !important;
}

.postreply {
    margin-left: 13px !important;
    }

td.highlight {
    padding-left: 3px !important;
    padding-bottom: 2px;
}

div[class="thread_hidden"] {
    width: 400px !important;
    margin-left: 5px !important;
    }

img.thumb {
    padding-left: 3px !important;
    padding-bottom: 2px;
}

td.highlight span.filesize {
    padding-left: 23px
}

td.highlight label {
    padding-left: 3px
}

blockquote {
    margin-left: 20px !important;
    padding-bottom: 5px;
    margin-top: 5px
}

/*Misc*/
div#updater.reply.dialog {
    position: fixed !important;
}

div#updater input {
    -moz-appearance: none;
}




div.reflinkpreview {
    border-radius: 0px;
}

a[href="mailto:sage"] {
    text-decoration: none !important;
}


a[href="mailto:sage"]:before {
    content: 'sage! ';
}

/* paginador */
table[border="1"]{
    position: fixed !important;
    top:320px !important;
    right: -316px !important;
    width: 360px !important;
    height:40px !important;
    background: #aaaacc !important;
    -moz-transition: right .1s ease-in-out 2s, opacity .2s ease-in-out 2s;
}

table[border="1"]:hover{
    position: fixed !important;
    right: 1px !important;
    -moz-transition: right .1s ease-in-out, opacity .2s ease-in-out;
}

/*buttons*/
#button_radio_details {
width:0px !important;
height:0px !important;
padding-left:17px !important;
padding-top:16px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s21.postimg.org/6u2el8zpf/note.png')  !important;
        }

img[src="/images/button-radio.gif"] {
width:0px !important;
height:0px !important;
padding-left:16px !important;
padding-top:15px !important;
margin-top:-2px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s28.postimg.org/5gfb4fzmx/button_radio2.gif')  !important;
        }
        
img[src="/images/button-close.gif"] {
width:0px !important;
height:0px !important;
padding-left:16px !important;
padding-top:13px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s28.postimg.org/ngb6mz4mh/close.png')  !important;
        }
        
img[src="/images/button-open.gif"] {
width:0px !important;
height:0px !important;
padding-left:13px !important;
padding-top:13px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s27.postimg.org/77rqlvl7j/plux.gif')  !important;
        }
        
img[src="/images/button-question.gif"] {
display: none !important
        }
        
img[src="/images/icon-report.png"] {
width:0px !important;
height:0px !important;
padding-left:13px !important;
padding-top:12px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s9.postimg.org/yewxphsnf/report.png')  !important;
        }
        
img[src="/images/button-expand.gif"] {
width:0px !important;
height:0px !important;
padding-left:15px !important;
padding-top:15px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s30.postimg.org/tlkqyjde5/expand_thread.png')  !important;
        }
        
img[src="/images/button-working.gif"] {
width:0px !important;
height:0px !important;
padding-left:15px !important;
padding-top:15px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s24.postimg.org/6g9xyj91t/button_working.gif')  !important;
        }

img[src="/images/button-paint.gif"] {
width:0px !important;
height:0px !important;
padding-left:15px !important;
padding-top:15px !important;
background-repeat:no-repeat !important;
background-position:bottom left !important;
background: url('http://s18.postimg.org/smz474g4l/button_paint.png')  !important;
        }

/*imgs*/
html {
    background: 
    url(/*[[mascot]]*/)fixed bottom 0px right no-repeat,  
    url(http://s28.postimg.org/6560zfygd/barr.png) fixed bottom 0px right -44px repeat-y !important;
    
}

div[style="margin-top: 5px"] {
    margin-left: 6px !important;
    margin-right: 160px !important;
    }
    
img[src="/images/icon-report.png"] {
}
    
/**//**//**//*Configurações de cores e fontes*//**//**//**/

/*default font*/
*, TD#postform_label_sage  {
    color: rgba(0,0,0,255) !important;
}

/*background*/
html {
    background-color: #eeeeee !important
}

/*spoiler*/
span.spoiler {
    color: #000 !important;
    background: #000 !important;
}
span.spoiler:hover {
    color: #ffffff !important;
}

/*replys*/
.postreply {
    background-color: #aaaacc !important;
    border: 0px solid #aaaacc !important;
    margin-top: -2px !important
}

td.highlight a {
    color: #000000 !important;
}

/*Bernd*/
.postername,.commentpostername, .admin {
    color: #3333cc !important;
    font-weight:bold !important;
}

/*Sage*/
.sage {
    color: #ff465c !important;
    font-weight:bold !important;
    font-size:13px !important;
}

/*subject*/
.postsubject {
    color: #cc3333 !important;
    font-weight:bold !important;
    font-size:14px !important;
    }


/*modpost*/
.post_modhighlight {
    color: #FF5959 !important;
}
/*boards*/
div[style="float: left"] {
    background-color: #dbdbdb !important;
    border-bottom: 1px solid #aaaacc  !important;
}

/*bold text*/
 .hidden_thread span, .hidden_thread, .omittedinfo   {
    color: #6e6e6e !important
}

/*thread number & reply buttom*/
 .postheader a{
    color: #00BFFF !important;
}

/*backlinks*/
u {
    color: #3DECFF!important;
    text-decoration: underline !important
}

/*links*/
blockquote a , td.rules a, div.thread a, span.reflink a {
    color: #2d2d99 !important;
}

/*quote*/
.quote {
    color: #007777 !important
    }

/*posts*/
form div table, form table {
    margin-bottom: 0px !important;
    margin-left: -10px !important;
    }
    
/*thread align*/
.thread {
    margin-top: 20px !important;
    }

/*ban*/
.ban_mark {
    color: #ff0000 !important;
    font-weight: bold !important;
}

/*postbox*/
table.postform textarea, input[tabindex="3"], input[tabindex="10"], input[name="captcha_secret"], #postform_submit {
	background-color: #f1f1f1 !important;
    border: 1px solid #e5e5e5 !important;
    }
    
/*checkbox*/
input[type="checkbox"] {
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    background-color: #f1f1f1 !important;
    border: 1px solid rgba(0,0,0,.2) !important;
    }

/*delet*/
div[style="position: absolute; right: 5px;"] {
    position: fixed !important;
    margin: -1px !important;
    width: 254px;
    height: 84px !important;
    top: 2px !important;
    right: 2px !important;
    background: #dbdbdb !important;
    text-align: center !important;
    opacity: 0;
    -moz-transition: all 0.5s ease;
    }
div[style="position: absolute; right: 5px;"]:hover {
    opacity: 1
    
    }
    
.file_thread{
   background: #dddddd !important;
   border: #dddddd 1px solid !important;
}

.file_reply{
   background: #9999bb !important;
   border: #9999bb 1px solid !important;
}

div.radio_idle
{
    margin-left: 6px!important;
    border: 2px solid #444444!important;
    background-color: #cccccc!important;

}

div.radio_onair
{
    margin-left: 6px!important;
    border: 2px solid #44aa44!important;
    background-color: #cceecc!important;

}

}
