css:
@-moz-document 
domain("dailymotion.com"), 
domain("dmcdn.net") {

.sd_header, .cookie_policy
{
background: -moz-linear-gradient(#0000FF, #000000)!important;
}
.button, .btn, .badge, .badge--tag, .validated 
{
background-color: #0000FF !important;
}
.recommended
{
background-color: #00FF00 !important;
}
.staff-pick
{
background-color: #00FFFF !important;
}
.tipsy.tipsy-button-dropdown .tipsy-inner, .tipsy-infotip .tipsy-inner, #popupContent .popup_title
{
background: -moz-linear-gradient(#0000FF, #000000)!important;
}
#content, .main-content, .pdg-btm-lg, #popupContent
{
background-color: #222222 !important;
}
#wrapper, input
{
background-color: #111111 !important;
}
.cookie_policy
{
border-top: 1px #0000FF solid !important;
}
.tipsy-inner
{
border: 1px #0000FF solid !important;
}
.btn-default-to-success, .btn, .mo_tabs
{
border-color: #0000FF !important;
}
.recommended, .staff-pick
{
color: #000000 !important;
}
.foreground
{
color: #0000FF !important;
}
a, p, input, h2, .ellipsis, .btn, .total,
.fg-no-skin, .utils_text-clamp, .video_views,
.js-tab-content, .tab-content, .link,
.video_description, .mrg-btm-xs, .video_owner,
.sub-menu-title, .mrg-btm-lg, .form_input label,
.dmco_html, .or-delimiter
{
color: #FFFFFF !important;
}
}
