css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("doitim.com") {
*{text-shadow: none !important;}    
    
.title{color: #888888 !important;background-color: #151515 !important;}
.tit{color: #C7EDCC !important;background-color: transparent !important;}
.icon{background-color: transparent !important;}
.sidebar li{border-top: none;border-bottom: none;}
    .sweetip{padding: 0 !important;}
    
input{color: #C7EDCC !important; background-color: transparent !important;}
textarea{color: #C7EDCC !important; background-color: #555555 !important;}
tbody{background-color: #252525 !important;}
    
div.end-at.ng-binding.more{color: #888888 !important;background-color: #151515 !important;}

a{color: #C7EDFF !important;background-color: transparent !important;}

ul{color: #888888 !important;background-color: #151515 !important;}

li{color: #888888 !important;background-color: #151515 !important;}
li.task .icon .mask{background: none !important;}
li.task .icon {background: none !important;}

h3{color: #888888 !important;background-color: #151515 !important;}

div.group{color: #888888 !important;background-color: #151515 !important;}
div.groups{color: #888888 !important;background-color: #151515 !important;}
div.toolbar{color: #888888 !important;background-color: #151515 !important; padding: 0 0 0 0;}
div.icon{color: #888888 !important;background-color: #151515 !important;}
div#task_container{color: #888888 !important;background-color: #151515 !important;}
div#switchbar{color: #888888 !important;background-color: #151515 !important;}
div.drag{background-color: #151515 !important;}
div.pri{background-color: #151515 !important;}
div.sep{display: none; margin: 0 0 !important; background-color: #151515 !important;}
div.inner{background-color: #555555 !important;}
div.popup{background-color: #151515 !important;}
div.ui-datepicker-header.ui-widget-header.ui-helper-clearfix.ui-corner-all{background-color: #888888 !important;}
div#right_date{background-color: #151515 !important;}
    
form.search-form{background-color: #151515 !important;}
    
H4.ng-isolate-scope{color: #888888 !important;background-color: #151515 !important;}
    
    .ui-datepicker table{background-color: #555555 !important;}   
    
    
}
