@-moz-document domain("deviantart.com") {

/* removes the join link */
   #activeArea a[href="http://www.deviantart.com/join"], #form-login A[href="https://www.deviantart.com/join/"] {
      display: none !important;
   }

/* always the same background image */
   body {
      background: #728776 url(http://st.deviantart.com/minish/main/gradient.gif) repeat-x scroll !important;
   }

/* hide banner on top of some pages */
   #ad-blocking-makes-fella-sad, .ad-blocking-makes-fella-confused {
      display:none !important;
   }

/* make login area smaller */
   #top-55, #top-collect {
      top : 0px !important;
      left: 170px !important;
      width: auto !important;
      right: 0px !important;
      position: absolute !important;
   }
   #top-collect  {
      margin-left: 20px !important;
   }
