      margin-top: -75px !important;
      margin-left: -15px !important;
      width: 100% !important;
      background-color: rgb(214, 222, 212) !important;
      border: 15px solid rgb(214, 222, 212) !important;
   }
   #output > DIV > CENTER {
      margin-top: -60px !important;
      position: absolute !important;
   }
   #sitback #window {
      margin-top: -40px !important;
   }
   #sitback #window + * {
      margin-top: -40px !important;
   }
   .bubbletop {
      -moz-border-radius-topright: 10px !important;
   }
   .bubbleprefix {
      top: 75px !important;
      width: 100%;
      -moz-box-sizing: border-box;
      position: absolute !important;
   }
   .bubbleprefix .tr {
      right: 0px !important;
      position: absolute !important;
   }
   #headerTop i.tr, #headerTop i.tl {
      top: 76px !important; 
   }

/* avoid blank boxes and empty space*/
   #midSection {
      top: 0px !important;
   }
