css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix("http://www.app-sales.net/"),
url-prefix("http://app-sales.net/")
{

body {
  color: #eee;
  background: #0F0F0F !important;
  background-image: url("https://img-fotki.yandex.ru/get/4123/161026817.0/0_acd93_38f25aaa_orig") !important;
  background-attachment: fixed !important;
  background-size: cover !important;
}

h1, h2, h3 {
  color: #AAA !important; 
  text-shadow: 1px 1px 1px #000 !important;
}

#infobar {
  background-color: #444 !important;
  opacity: 1 !important;
}

#inner-wrapper {
  background-color: #2A2A2A !important;
  opacity: 0.9 !important;
}

.sidemodule p {
  color: #eee !important;
}

.wrench a {
  background-color: gray !important;
  opacity: 0.5 !important;
}

.deal, .sidemodule, .module, .nae {
  background: rgba(0, 0, 0, 0.6) !important;
}

.slideup {
  background: none !important;
}

.gplus_widget {
  background-color: #1A1A1A !important;
  opacity: 0.2 !important;
  display: none !important;
  margin-bottom: 35px !important;
}

.appname a {
  color: #ccc !important;
}

#filters input[type='text'] {
  color: #fff !important;
}

#footer {
  color: #999 !important;
}

#footer a {
  color: #ccc !important;
}

.ratingbar {
  background: #FF2424
}

#logo {
  filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale"); /* Firefox 10+, Firefox on Android */
  filter: gray; /* IE6-9 */
  -webkit-filter: grayscale(100%); /* Chrome */
}

#header-wrapper {
  border-bottom: 3px solid #444 !important;
  opacity: 0.9 !important;
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAABWCAYAAAAOnvzGAAAACXBIWXMAAAsTAAALEwEAmpwYAAAATElEQVQY05XMQQ4AIQwCQOBp/f+biheTNdpq9kJSQgcRYZGEAMywjb37H5Vi+8XbvpxtJ2nlt1310aKV0nb3MzNnkDyobwJg3VUoSQydM2Ucm2mcewAAAABJRU5ErkJggg==) !important;
}

.stars {
  background-image: url(data:image/png;base64,) !important;
}

}
