css:
  @-moz-document domain("nytimes.com") {
  
  /* this first rule give you an idea how completely fucked their markup is */  
  table#mainTable { width:2000px!important; }  
  
  div#main { 
    min-width:100%!important;
    width:900px!important;
  }
  
  /* adblocking */
     #adxLeaderboard,
     #adxSponLink,
     #adxToolSponsor,
     #adxBigAd,
     #adxDelivery,
     #adBar,
     #bColumn,
     #HPMiddle,
     #Middle5,
     #Middle4,
     #Middle1,
     .moduleAd,
     #homeAd1,
     #homeAd2
  	{display: none !important;}

  /* Main */
     #aColumn {
  	float: none !important;
  	width: auto !important;
  	padding: 10px 30px !important;
  	text-align: justify !important;}
     #main {background: none !important;}
     #article {width: auto !important;}
     div#pageLinks {font-size:20px!important;  text-align:center;}
  /* article extras */
     .articleTools {margin-left: 1em !important;}
     ul.toolsList li:last-child {border-bottom: none !important;}
     .credit {text-align: left !important;}
     #articleExtras {display: none !important;}
     div.toolsContainer {display:none!important}

  /* archive articles */
     td > p {text-align: justify !important;}
     .date {display: none !important;}

  /* Move cColumn up */
     #belowFoldTable .cColumn {
  	position: absolute !important;
  	top: 505px !important;
  	left: 615px !important;
  	height: auto !important;
  	border-left: none !important;
  	z-index: 5 !important;}
     #belowFoldTable .listing
  	{width: 30% !important;}

     #showtimesAndTicketsSearch {display: none !important;}
     #edPromo {height: auto !important;}

     .columnGroup,
     .subColumnA,
     .subColumnB {
  	border-left: none !important;
  	border-right: none !important;
  	border-top: none !important;}
     .columnGroup {
  	margin-bottom: 0 !important;
  	margin-top: 0 !important;}
     .cColumn #Box1 {display: none !important;}
     .cColumn .singleColumn {display: none !important;}

  /* Remove clutter */
     #homepageTools,
     .cColumn table,
     #classifiedsWidget
  	{display: none !important;}

     div#login,
     ul#memberTools,
     ul#memberTools+ul.tabs
  	{display: none !important;}

  /* Remove three tabs from navigation panel */
     #navJobs,
     #navRealestate,
     #navAutos 
  	{display: none !important}
     #navWorld,
     #navUS,
     #navNYRegion,
     #navBusiness,
     #navTechnology,
     #navScience,
     #navHealth,
     #navSports,
     #navOpinion,
     #navArts,
     #navStyle,
     #navTravel
  	{width: 77.3px !important}
  	
  	.abColumnWrapper { display:none!important; }

    div#aColumn h1 { font-size:40px!important;}
    div.firstColumnGroup { display:none!important;}
    div.borderBottomColumnGroup { display:none!important;}
    div.cColumn { display:none!important; }
    ul.advertisementThumbnails { display:none!important;}
    div#page div#main div#belowFold { background: none!important;}
    div#page div#main div#belowFold table#belowFoldTable { background: none!important;}
    td.listing li h4 { font-size:18px!important;}
    td.listing li ul li a { font-size:12px!important;}
    div#wsodMarkets { display:none!important; }
    table#belowFoldTable .cColumn div.columnGroup.Bottom1 { display:none!important;}
  }
