css:
@-moz-document domain("blackarch.org"), domain(blackarch.org), domain(blackarch), domain(www.blackarch.org)
{
    *{
      margin: 0;
      padding: 0;
    }*
    
    body {
      font: 80% Arial, Helvetica, Sans-Serif;
      color: #000000;
      background: #FFFFFF;
      line-height: 1.6em;
    }
    
    a {
      text-decoration: none;
      color: #C40000;
    }
    
    a:hover {
      color: #808080;
    }
    
    a:focus {
      outline: 0;
    }
    
    h1 {
      font-size: 2.8em;
      line-height: 35px;
    }
    
    h2 {
      color: #000;
      font: bold 150% Arial, Sans-Serif;
      letter-spacing: -1px;
      padding: 0;
      margin: 0 0 0px;
    }
    
    h3 {
      text-decoration: none;
      color: #C40000;
      font: bold 150% Arial, Sans-Serif;
      letter-spacing: -1px;
      padding: 0;
      margin: 0 0 0px;
    }
    
    h4 {
      color: #f4f4f4;
      letter-spacing: -1px;
      padding: 0;
      margin: 0 0 0px;
    }
    
    d {
      float: right;
    }
    
    img {
      border: 0;
    }
    
    .grey {
      color: #565656;
    }
    
    div.box {
      border: 1px solid #ccc;
      border-radius: 10px;
      padding: 7px;
      font-size: .9em;
      background: #f4f4f4;
    }
    
    code {
      border: 1px solid #ccc;
      border-radius: 5px;
      background-color: #EFEFEF;
      padding: 1px 3px; -webkit-border-radius: 2px;
      border-radius: 2px;
    }
    
    ul {
      list-style-type: none;
    }
    
    ol {
      list-style-type: none;
    }
    
    #wrap {
      width: 95%;
      margin: 10px auto 0;
    }
    
    #logo {
      float: left;
      padding-top: 12px;
      width: 100px;
      background: url("http://blackarch.org/images/logo.png") center left no-repeat;
      margin: 0 0 7px;
    }
    
    #header_top {
      color: #454545;
      clear: both;
      height: 74px;
    }
    
    #header_top p {
      padding: 0;
      margin: 0;
    }
    
    #header_top ul {
      float: right;
      padding: 15px 0 0 0;
      margin: 0 0 10px 0;
    }
    
    #header_top ul li {
      list-style-type: none;
      display: inline;
      font-weight: bold;
      font-size: 1.4em;
    }
    
    #header_top ul li a {
      padding-left: 7px;
      margin-right: 5px;
      color: #C40000;
    }
    
    #header_top ul li a:hover {
      padding-left: 7px;
      margin-right: 5px;
      color: #808080;
    }
    
    #header_bottom {
      background: #454545;
      color: #EBEBEB;
      padding: 20px 240px 20px 10px;
      clear: both;
      border-left: 2px solid #fff;
      border-right: 2px solid #fff;
      margin: 0 0 1px 0;
      height: 190px;
    }
    
    #header_bottom h2 {
      color: #F3F2BF;
      width: 400px;
      margin: 0 0 10px;
      background: #FFFFFF;
    }
    
    #header_bottom p a {
      color: #808080;
    }
    
    #header_bottom p a:hover {
      color: #C40000;
    }
    
    #header_search {
      background: #FFFFFF;
      color: #EBEBEB;
      padding: 60px 240px 20px 20px;
      clear: both;
      border-left: 2px solid #fff;
      border-right: 2px solid #fff;
      margin: 0 0 1px 0;
      height: 70px;
    }
    
    #bar {
      clear: left;
      width: 100%;
      border-top: 2px solid #C40000;
      margin: 0;
      padding: 5px 0 0 0;
    }
    
    #maincontent {
      padding: 20px 0;
      clear: both;
      color: #777777;
    }
    
    #left {
      float: left;
      width: 700px;
      padding: 5px 15px 0 5px;
      margin: 0 0 25px 0;
    }
    
    #left p {
      margin: 0 0 15px;
    }
    
    #right {
      width: 300px;
      float: right;
    }
    
    #applink {
      float: right;
    }
    
    #info {
      border: 1px solid #ccc;
      padding: 7px;
      font-size: .9em;
      background: #FFFFFF;
    }
    
    #info b {
      font-size: 1.6em;
    }
    
    #dl_body {
      border: 1px solid #ccc;
      padding: 7px;
      background: #FFFFFF;
      color: #cccccc;
    }
    
    #footer {
      clear: both;
      padding: 10px;
      margin: 10px 0 0 0;
      border-top: 1px solid #ccc;
      line-height: 2em;
      text-align: center;
    }
    
    #tbl-minimalist {
      font-size: 11px;
      background: #FFFFFF;
      margin: 20px;
      width: 95%;
      border-collapse: collapse;
      text-align: left;
    }
    
    #tbl-minimalist th {
      font-size: 11.5px;
      font-weight: bold;
      color: #FFFFFF;
      padding: 4px 15px;
      border-bottom: 2px solid #C40000;
    }
    
    #tbl-minimalist td {
      border-bottom: 1px solid #ccc;
      color: #FFFFFF;
      padding: 2px 2px;
    }
    
    #tbl-minimalist tbody tr:hover td {
      color: #C40000;
    }
}
