css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("e-klase.lv") {

/* making everything look (almost) nice on a screen with (almost) any width */
body {
    height: auto !important;
}

#bc_wrap,
#content_wrap,
#top_adv_box_r {
    width: auto !important;
    max-width: 800px;
}

.top_nav {
    overflow-x: hidden !important;
}

/* hiding useless stuff */
/* fuck you guys (they detect the presence of the extension by checking
    #footer's 'display') */
#footer {
    width: 0px !important;
    height: 0px !important;
}

#top_adv_box,
#top_adv_box_r,
#top_sub_nav,
#idx_content,
.open_faq,
.q,
#mn_dbg,
#fake_ph_uname,
#fake_ph_pass,
#slide_wrap,
.todays_names,
#btn_live_demo,
#wall_left,
#wall_right {
    display: none !important;
}

/* relocating the search etc since we threw away the PIESAKIETIES
   PREZENTACIJAI button */
#btn_search {
    right: 1px !important;
}

#btn_archive {
    right: 109px !important;
}

.soc_icons {
    right: 176px !important;
}

/* some styling changes to make everything look cute */
#login_box_main {
    background: #86BF3C !important;
    border-radius: 10px !important;
    width: 100% !important;
    height: 145px !important;
    top: 100px !important;
}

#eklase_login input {
    width: 95% !important;
    left: 2.5% !important;
    border: 2px solid #658F4A !important;
    border-radius: 10px !important;
    background: white !important;
}

#eklase_login button {
    background: #658F4A !important;
    border-radius: 10px !important;
    width: 95% !important;
    left: 2.5% !important;
}



}
