@-moz-document domain("youtube.com") {
#logo-container, #logo-container #logo, #logo-container .logo {
	display: inline-block !important;
	padding: 0px !important;
    width: 118px !important;
    height: 36px !important;
    min-width: 118px !important;
    min-height: 36px !important;
    max-width: 118px !important;
    max-height: 36px !important;
	margin-top: -1px !important;
	margin-bottom: -5px !important;
	padding-right: 15px !important;
}
#logo-container .logo, #logo-container #logo {
	background: url(http://oi43.tinypic.com/vgizgz.jpg) 0 0 no-repeat !important;	
}
#logo-container .content-region {
	position: relative !important;
	left: 120px !important;
	top: -33px !important;
	padding: 0px !important;
	margin: 0px !important;
}
#appbar-guide-button {
	position: relative !important;
	top: 1px !important;
	left: 3px !important;
}
div.yt-masthead-logo-container {
	width: 200px !important;
    height: 30px !important;
}
#footer-links-secondary {
	position: relative;
}
#footer-links-secondary li:last-child {
	position: absolute !important;
	display: block !important;
	background: url("http://i43.tinypic.com/210f6es_th.gif") no-repeat 0 0 !important;
	width: 160px !important;
	height: 160px !important;
	z-index: 1000 !important;
	text-indent: -1000em !important;
	top: -118px !important;
	right: 0px !important;
}
#footer-logo span, #footer-logo img {
	position: relative !important;
	display: block !important;
	background: url(http://oi43.tinypic.com/vgizgz.jpg) -45px 0 no-repeat !important;
	padding-left: 73px !important;
	padding-top: 36px !important;
    width: 0px !important;
    height: 0px !important;
	top: 13px !important;
}
.branded-page-v2-col-container {
	border-right: 0px !important;
}
.branded-page-v2-top-row {
	border: 0px !important;
}
