}
#appbar-guide-menu, .guide-flyout, .site-center-aligned .yt-card, .site-center-aligned .hh #yt-admin-content, #masthead-appbar, .watch-sidebar, .watch-content, #non-appbar-vm-video-actions-bar .vm-video-actions-inner, .vm-list-view .vm-video-list li, #non-appbar-vm-video-actions-bar, #non-appbar-vm-video-actions-bar .vm-video-actions-bar, #vm-pagination, .vm-label-item, .vm-member-item, #yt-admin-content, .playlist-video-item.odd, .playlist-video-item:nth-child(2n+1),#watch7-creator-bar, #watch-response, #watch7-headline, #watch7-user-header, #watch7-action-buttons, #watch7-action-panels, #watch7-container, #dashboard-header, .dashboard-widget.notification, .dashboard-widget .dashboard-widget-content, .dashboard-widget .dashboard-widget-config {
	background: #f9fef6 !important;
	border-color: #e1f4d4 !important;
}
.playlist-video-item.even, .playlist-video-item:nth-child(2n), #subscription-manager-container .even td, .watch-editable:hover {	
	background: #e1f4d4 !important;
	border-color: #e1f4d4 !important;
}
.vm-list-view .vm-video-metrics {
	background: linear-gradient(to right, #e1f4d4 0px, #f9fef6 11px) !important;
	border-color: #e1f4d4 !important;
}
#yt-masthead-container, #footer-container, #masthead-expanded-container, #comment-settings .comment-settings-header, .commenthub .comment-entry:hover {
