	background: #e6cc4c !important;
	color: #fffef5 !important;
	text-shadow: #c8ab20 -1px -1px 0px !important;
}
.guide-flyout-trigger.on-hover, #guide-container .guide-item:hover, #guide-container .guide-collection-item:hover .guide-item {
	background: #d88fb8 !important;
	color: #fffef5 !important;
}
.yt-uix-button-subscribe-branded, .yt-uix-button-subscribe-branded[disabled], .yt-uix-button-subscribe-branded[disabled]:hover, .yt-uix-button-subscribe-branded[disabled]:active, .yt-uix-button-subscribe-branded[disabled]:focus {
	background: linear-gradient(to top, #e6cc4c 0px, #c8ab20 100%) !important;
	color: #fffef5 !important;
	text-shadow: #c8ab20 1px 1px 0px !important;
}
.yt-uix-button-subscribe-branded .yt-uix-button-icon-subscribe {
	background-image: url(http://oi43.tinypic.com/2qvyus6.jpg) !important;
    background-position: 0px -33px !important;
}
.epic-nav-item-heading,.feed-header-feed-filter .yt-uix-button-epic-nav-item.selected, .feed-header-feed-filter .yt-uix-button-epic-nav-item:hover,.epic-nav-dropdown-group:hover, a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading, .commenthub .tab.active, .menu-tab:hover, .menu-tab.selected  {
