	box-shadow: none !important;
	border-top: 0px !important;
}
#watch-description-toggle .yt-uix-button, #watch7-action-panel-footer hr {
	border-color: #e1f4d4 !important;	
}
#watch7-action-panel-footer {	
	border-color: #e1f4d4 !important;	
	background: #f5fdf0 !important;
}
.yt-uix-button-default:hover, .yt-uix-button-text:hover {
