}
.actionable .yt-uix-button:active .yt-uix-button-icon-watch-like, .actionable .yt-uix-button.yt-uix-button-toggled .yt-uix-button-icon-watch-like, .yt-uix-button-toggled .yt-uix-button-icon-playlist-like {
	background-image: url(http://oi43.tinypic.com/2qvyus6.jpg) !important;
    background-position: 2px 2px !important;
}
#masthead-expanded .masthead-expanded-menu-item a, .yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a, .yt-uix-button-link, .multirow-shelf .yt-uix-expander-head, .feed-item-container:hover .feed-item-header a, .feed-item-container:hover a.yt-user-name, .feed-header .feed-manage-link:hover, .yt-lockup-mini:hover .yt-lockup-title, .vm-list-view .vm-video-title a.vm-video-title-content:hover, .playlist-video-item .video-title, #watch7-sidebar .video-list-item a:hover .title, #watch-response-header-content p a:hover, .commenthub .comment-header .user-name, .commenthub .comment-entry:hover .video-title-content {	
