}

#masthead-expanded .playlist-data-section, .yt-pl-thumb-link .yt-pl-thumb-overlay, .yt-pl-thumb .sidebar, .playlist-thumb-strip .hover-overlay, .playlist-thumb-strip .video-count-box, #masthead-expanded .play-all  {
	background: rgba(202, 121, 169, 0.8) !important;
}
.video-time, .watched-badge, .video-index, .video-thumb-watched {
	background: #ca79a9 !important;
}
#masthead-expanded .list-video-count {
	color: white !important;
}

/* Player. Start. */
.html5-player-chrome, .html5-info-bar, .ytp-menu, #watch-appbar-playlist {
	background: #076352 !important;
}
#watch-appbar-playlist .control-bar {
	border-color: #055a4a !important;
}
#watch-appbar-playlist .playlist-videos-list>li:hover, #player .ytp-playlist-tray-item-current:hover {
	background: #9edd7e !important;
}
#watch-appbar-playlist .playlist-videos-list>li.currently-playing, #player .ytp-playlist-tray-item-current {
  	background: #89ea64 !important;
}
#watch-appbar-playlist .playlist-videos-list>li, #player .ytp-playlist-tray-item {	
	background: #7edd5a !important;
	border-color: #7fd360 !important;
}
#watch-appbar-playlist .playlist-videos-list>li .yt-ui-ellipsis,
#watch-appbar-playlist .playlist-videos-list>li:hover .yt-ui-ellipsis, 
