	background: transparent !important; 
}
#watch-appbar-playlist .playlist-videos-list>li .video-index, #watch-appbar-playlist .playlist-videos-list>li:before {
	color: #0b5845 !important;
}
#watch-appbar-playlist .playlist-videos-list>li .yt-ui-ellipsis, #player .ytp-playlist-tray-item-title, #player .ytp-playlist-tray-item {
	color: #0b5845 !important;
