}
#watch-appbar-playlist .video-uploader-byline, #player .ytp-playlist-tray-item-current .ytp-playlist-tray-item-author {
	color: #117059 !important;
	text-shadow: #7edd5a -1px -1px 0px !important;
}
#watch-appbar-playlist .author-attribution, .ytp-time-display {
	color: #c2f7a7 !important;
}
.site-center-aligned .yt-scrollbar-dark ::-webkit-scrollbar-thumb  {
  	background: #076352 !important;
    border: 0px !important;
}
.site-center-aligned .yt-scrollbar-dark ::-webkit-scrollbar-track {
    background: #0a866e !important;
    border: 0px !important;
}
/* Player. End. */
a {
	color: #40ab4b;
}
a.user-name, a.yt-uix-redirect-link, div.action-panel-content a.yt-uix-sessionlink, a.legacy-parent-link, span.like-count, span.hide-comments a, div.comment-text a, a.show-more {
