/* i really want this to be global */
@charset "UTF-8";
/*:█▬█(۞)▀█▀|||||||||||█▀█▀█||||||||||█▬█(۞)▀█|||||▀█▬█(۞)▀█▀ ^^@@^^ █▬█(۞)▀█▀/// v1.0 | 2014 */
* {
margin: 0;
padding: 0;
border: 0;
outline: none;
vertical-align: baseline;
font: fantasy;
box-sizing: border-box;
background: rgba(0, 0, 0, 0);
background-color: rgba(0, 0, 0, 0);
border-color: rgba(0, 0, 0, 0.5)!important;
-webkit-font-smoothing: antialiased;
}
html,body,div,span,applet,object,iframe,h1,h2,h3,h4,h5,h6,p,blockquote,pre,a,abbr,acronym,address,big,cite,code,del,dfn,em,font,img,ins,kbd,q,s,samp,small,strike,strong,sub,sup,tt,var,b,u,i,center,dl,dt,dd,ol,ul,li,fieldset,form,label,legend,table,caption,tbody,tfoot,thead,tr,th,td,button{margin:0;padding:0;border:0 none;outline:0;font-size:100%;vertical-align:baseline;background:transparent;}
body{line-height:1;}
ol,ul{list-style:none;}
blockquote,q{quotes:none;}
blockquote:before,blockquote:after,q:before,q:after{content:'';content:none;}
:focus{outline:0;}
ins{text-decoration:none;}
del{text-decoration:line-through;}
table{border-collapse:collapse;border-spacing:0;}
/* /set */

/* #d93636 red */

html {
background-color: rgb(26, 32, 186)!important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0, 0, 0, 0.5)), color-stop(50%,rgba(255, 255, 255, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.5)));
background-attachment: fixed;
background-repeat: no-repeat;
background-size: cover;
height: 100%;
}
body {
background: rgba(0, 0, 0, 0)!important;
background-color: rgba(0, 0, 0, 0)!important;
}
body, td, div, ul, ol, button, textarea, input {
font:normal 12px/20px helvetica,arial,sans-serif;
color:#D3D3D3;
}
h1,h2,h3,h4,h5,h6 {
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(26, 32, 186)), color-stop(50%,rgba(26, 32, 186, 0.5)), color-stop(100%,rgb(26, 32, 186)));
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(26, 32, 186)), color-stop(50%,rgba(26, 32, 186, 0.5)), color-stop(100%,rgb(26, 32, 186)));
color: rgb(232, 253, 253)!important;
text-shadow: 0 1px 0 rgb(0, 0, 0), 0 2px 1px rgba(0, 0, 0, 0.8), 0 3px 2px rgba(0, 0, 0, 0.6), 0 4px 3px rgba(0, 0, 0, 0.4);
}
ol li a {
background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(211, 211, 211, 0.5)), color-stop(50%,rgba(163, 163, 163, 0.5)), color-stop(100%,rgba(128, 128, 128, 0.5)));
background-image:-webkit-gradient(linear, left top, left bottom, color-stop(0%,#a7cece), color-stop(100%,#688b8b));
color:#dfdfdf!important;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.4), 0 2px 1px rgba(0, 0, 0, 0.3), 0 3px 2px rgba(0, 0, 0, 0.2), 0 4px 3px rgba(0, 0, 0, 0.1);
}
h4 a, h5 a, h6 a {
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255, 255, 255, 0.5)), color-stop(50%,rgba(128, 128, 128, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.5)));
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(255, 253, 253, 0.5)), color-stop(50%,rgba(128, 128, 128, 0.5)), color-stop(100%,rgba(0, 0, 0, 0.5)));
color: rgb(239, 255, 176)!important;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.8), 0 2px 1px rgba(0, 0, 0, 0.7), 0 3px 2px rgba(0, 0, 0, 0.6), 0 4px 3px rgba(0, 0, 0, 0.5);
}
strong, b {
font-weight: bold;
color: rgb(97, 167, 221)!important;
}
button, textarea, input {
font: 400 12px/18px arial;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(23, 30, 30, 0.5)), color-stop(100%,rgba(8, 11, 11, 0.5)));
color: rgba(255, 255, 255, 0.85);
resize: none; 
white-space: pre-wrap;
word-wrap: break-word;
}
div, html, section, canvas, video, header {
box-sizing: content-box;
-webkit-box-sizing: content-box;
}
div:not([onclick]) {
background-color: rgb(11, 45, 229)!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 100% 1px;
background-repeat: repeat-y;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
text-shadow:-1px 1px rgba(55,55,55,0.5);
outline-offset: inherit;
outline-color: rgb(26, 32, 186)!important;
outline-style: ridge;
outline-width: initial;
}
div:not([class]) {
background-color: rgb(11, 45, 229)!important;
background: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 1px 100%;
background-repeat: repeat-x;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
text-shadow:0 -1px 1px rgba(55,55,55,0.5);
outline-offset: inherit;
outline-color: rgb(26, 32, 186)!important;
outline-style: double;
outline-width: initial;
}
div:not([emty]) {
background-color: rgb(26, 32, 186)!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.35) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 100% 1px;
background-repeat: repeat-y;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: rgb(26, 32, 186)!important;
outline-style: ridge;
outline-width: initial;
}
div:not([id]) {
background-color: rgb(26, 32, 186)!important;
background: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 1px 100%;
background-repeat: repeat-x;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
text-shadow:1px -1px rgba(55,55,55,0.5);
outline-offset: inherit;
outline-color: rgb(26, 32, 186)!important;
outline-style: double;
outline-width: initial;
}
span:not(emty) {
background-color: #1A20BA!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 100%;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color:#4E9CB5;
outline-style: ridge;
outline-width: medium;
}
span:not([id]) {
background-color: #1A20BA!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 100%;
background-clip: padding-box;
background-origin: content-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: #02779E;
outline-style: ridge;
outline-width: medium;
}
span:not([onclick]) {
background-color: #1A20BA!important;
background-image: -webkit-linear-gradient(top,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 50%,rgba(128, 128, 128, 0.5) 100%);
background-size: 100% 100%;
background-repeat: no-repeat;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: #4E9CB5;
outline-style: initial;
outline-width: initial;
}
span:not([class]) {
background-color: #1A20BA!important;
background-image: -webkit-linear-gradient(top,rgba(255, 255, 255, 0.5) 0%,rgba(0, 0, 0, 0.5) 50%,rgba(128, 128, 128, 0.5) 100%);
background-size: 100% 100%;
background-repeat: no-repeat;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: #02779E;
outline-style: initial;
outline-width: initial;
}
li:not([id]):not([onclick]) {
background-color: #0B2DE5!important;
background: -webkit-linear-gradient(top,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 1px 100%;
background-repeat: repeat-x;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: rgb(26, 32, 186)!important;
outline-style: double;
outline-width: thick;
}
li:not([emty]):not([class]) {
background-color: #0B2DE5!important;
background-image: -webkit-linear-gradient(left,rgba(0, 0, 0, 0.5) 0%,rgba(255, 255, 255, 0.5) 50%,rgba(0, 0, 0, 0.5) 100%);
background-size: 100% 1px;
background-repeat: repeat-y;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
outline-offset: inherit;
outline-color: #02779E;
outline-style: outset;
outline-width: initial;
}
li > a {
background-color: #0B2DE5!important;
background: -webkit-linear-gradient(top,rgba(128, 128, 128, 0.5) 0%,rgba(192, 192, 192, 0.5) 50%,rgba(255, 255, 255, 0.5) 100%);
background-size: 1px 100%;
background-repeat: repeat-x;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
}
ul:not([id]):not([onclick]) {
background-color: #0B2DE5!important;
background: -webkit-linear-gradient(top,rgba(128, 128, 128, 0.5) 0%,rgba(192, 192, 192, 0.5) 50%,rgba(255, 255, 255, 0.5) 100%);
background-size: 1px 100%;
background-repeat: repeat-x;
background-clip: content-box;
background-origin: padding-box;
color: rgba(255, 255, 255, 0.8);
}
button {
background:#0B2DE5 none no-repeat scroll 0px center;
background-color: #0B2DE5!important;
background-image:-webkit-gradient(linear, left top, right bottom, color-stop(0%,rgb(48, 60, 60)), color-stop(100%,rgb(8, 11, 11)));
}
p > a {
color: rgb(219, 219, 219)!important;
}
p {
color: rgb(185, 185, 185)!important;
background-color: rgba(31, 31, 31, 0.5);
background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(23, 30, 30, 0.5)), color-stop(100%,rgba(8, 11, 11, 0.5)));
}
a {
background-color: rgba(0, 0, 0, 0)!important;
color: rgb(51, 51, 51);
color: rgb(215, 223, 234)!important;
cursor: pointer;
text-shadow: 0 1px 0 rgba(0, 0, 0, 0.5), 0 2px 1px rgba(0, 0, 0, 0.4), 0 3px 2px rgba(0, 0, 0, 0.3), 0 4px 3px rgba(0, 0, 0, 0.2);
text-decoration: none;
}
a:hover{
outline: none!important;
color:rgba(255,255,255,1)!important;
text-shadow:0 1px 1px rgba(55,55,55,0.5);
-webkit-transition:all 250ms;
}
img {
border: 1px none rgba(61, 61, 61, 0.25);
border-radius: 3px;
box-shadow: inset 0 0 1px 1px rgba(225, 225, 255, 0.6), 0 10px 15px rgba(2, 2, 2, 0.5);
padding: 1px 2px 3px;
background-color: #2BB2DD;
background-image: -webkit-gradient(linear, left top, right bottom, color-stop(0%,rgba(82, 82, 82, 1)), color-stop(50%,rgba(182, 182, 182, 0.85)), color-stop(100%,rgba(25, 25, 25, 1)));
}
a:hover img {
  -webkit-transform:scale(1.1);
  -webkit-transition: -webkit-transform 240ms;
}
div#contentCol.homeFixedLayout {
  background-color: rgb(26, 32, 186)!important;
}
.fbPhotoSnowlift .stage .fbPhotosPhotoTagboxes {
border: rgba(255, 255, 255, 0.1) 1px solid;
-webkit-box-sizing: border-box;
font-size: 11px;
display: none!important;
}
#navSearch .uiSearchInput input {
padding-top: 0px;
color: rgb(0, 0, 0)!important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(182, 182, 182, 0.5)), color-stop(100%,rgba(255, 255, 255, 0.5)))!important;
}
.uiTypeahead .uiSearchInput {
background-color: rgba(255, 255, 255, 0)!important;
}
.uiMentionsInput .mentionsTextarea, .uiMentionsInput .mentionsTypeahead {
color:rgba(255, 255, 255, 0.8)!important;
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(14, 240, 222, 0.5)), color-stop(100%,rgba(38, 12, 233, 0.5)))!important;
zoom: 1;
}
.fbFeedTicker .tickerStoryActive, .fbFeedTicker .tickerStoryClickable:hover {
background-color: rgb(0, 85, 255) !important;
border-bottom-color: rgb(49, 117, 252) !important;
}

.CodeMirror[data-size="normal"] div, .CodeMirror[data-size="normal"] pre {
background: rgb(169, 169, 169)!important;
}
.CodeMirror-wrap .CodeMirror-code pre {
background-color: rgb(105, 105, 105)!important;
}
pre > span {
background: lightgray!important;
}
