css:
@-moz-document domain("detail.chiebukuro.yahoo.co.jp") {
#msthdtp, .msthdlink, #common-navi, #sub, #footer, .socialBtn,
.ask-botton, .md_listCmmn, .mdListcmmn, #chie-ams,
#sqb_main, hr+div[style], #im_main, .mdAdTxtExtnsn, #vo_main,
.sclBttn, #mainSearchWrap, #h_nav, .actionContents, .topicPass,
.shareBtnList, #yjSLink, .mdLightForm
{ display: none !important; }

body {
  background: #f5f5f5;
}

#wrapper {
  background: #fff;
  margin: 40px auto;
  padding: 40px;
  border-radius: 3px;
  box-shadow: 0 1px 3px #ddd;
}

.mdPstd{
  border-top: 0 !important;
}
h1 {
  font-size: 20px !important;
  padding: 2em 0 !important;
  border-top: 0 !important;
}

.yj950-1 #wrapper, .yj950-2 #wrapper, .yj950-3 #wrapper {
  width: 638px !important;
}

.mdPstdQstn {
    margin: 2em 0 4em;
}
}

@-moz-document domain("q.hatena.ne.jp") {
#header-body>form, #nav, #breadcrumbs, #aside, [class*=google],
.double-rectangle, #footer, #footer-page-to-top, ins, .answer-comment-form
{ display: none !important; }

#header {
  margin: 2em 0 3em;
}

#container {
  width: 700px !important;
}

#section-body {
  margin: 0 !important;
}

body {
  font-family: sans-serif !important;
}

pre {
  font-size: 13px !important;
}
}

@-moz-document domain("oshiete.goo.ne.jp") {
#gheader, body>header, body>nav, #modal_show_area,
#os_rside, #are_search_footer_popup,
#no_a_area, #connection_area, .con_article, #category_contents,
.pagetop, .broadFooter, footer,
#crumb, .businessanswer, .q_only_remark_item, .q_only_item,
.remark_complain
{ display: none !important; }

#os_contents
{ float: none !important; }

#os_inmain {
  margin: 0 auto;
  width: 800px;
}

.q_article_info h1 {
  font-size: 1.5em !important;
}
.a-thanks p {
  font-size: 1em !important;
}

}
