css:
@-moz-document url-prefix("https://twitter.com") {
*{
  -webkit-border-radius: 0!important;
          border-radius: 0!important;
  -webkit-box-shadow: none !important;
          box-shadow: none !important;
    text-shadow: none !important;
         border: none !important;
}
  
.flex-module{
      display: none !important;
    }
    
.dashboard{
        position: fixed !important;
        top: 46px;
        z-index: 999;
        width: 66.03% !important;
        height: 55px !important;
      }
      
.content-main{
          width: 100% !important;
        }
    
.home-tweet-box, .rosetta .dm-tweetbox, .rosetta .WebNotification-buffer--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox{
        background: #FFFFFF !important;
      }
  
.home-tweet-box, .rosetta .dm-tweetbox, .rosetta .WebNotification-buffer--altColor, .content-main .conversations-enabled .expansion-container .inline-reply-tweetbox{
      border-bottom: 2px solid #eee !important;
    }

.new-tweets-bar{
    top: -4px !important;
  }
  
.session .dropdown-menu, .global-nav .pull-right .dropdown-menu, .global-nav .secondary-nav .dropdown-menu{
        position: absolute !important;
        z-index: 1000 !important;
      }

.topbar-tweet-btn{
    display: none !important;
  }
}
