/*
 * Make Sponsored Ads More Visible On Google 
 *
 * Updated by  : paisionut.com
 * Author      : Pais Ionut (paisionut.com)
 * Created on  : 23-01-2014
 * Version     : 1.5 (21-02-2014)
 * Description : Modifies the background color of ads results on Google Web Search (http://www.google.com/)
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */
/* AGENT_SHEET */
@-moz-document url-prefix("http://www.google"), url-prefix("https://www.google") {
#tads, #tadsb {
 background-color: #fff !important;
}
#mbEnd h3, #tads h3, #tadsb h3 {
 color: #4385f5 !important;
}
#mbEnd h3 > a:link, #tads h3 > a:link, li.ads-ad h3 > a {
 color: #4385f5 !important;
 text-decoration: none !important;
 border-bottom: 1px solid #f5b400 !important;
}
#mbEnd .ads-ad, #tads .ads-ad, #tadsb .ads-ad {
 color: #333333 !important;
 border: 1px solid #ed9c98 !important;
 margin: 10px 5px 5px 0px !important;
 padding: 10px 10px 10px 10px !important;
}
#mbEnd .ads-ad:hover, #tads:hover .ads-ad:hover, #tadsb .ads-ad:hover {
 color: #333333 !important;
 border: 1px solid #a0c2ff !important;
 margin: 10px 5px 5px 0px !important;
 padding: 10px 10px 10px 10px !important;
 -moz-box-shadow: 0 0 5px rgba(0,0,0,0.2) !important;
 -webkit-box-shadow: 0 0 5px rgba(0,0,0,0.2) !important;
 box-shadow: 0 0 5px rgba(0,0,0,0.2) !important;
 background-color: #fbfbfb !important;
 cursor: pointer !important;
}
#center_col .ads-visurl {
 color: #109d59 !important;
}
#center_col ._p {
 color: #000 !important;
}
#center_col ._je, #center_col ._t, #center_col ._u {
 color: #333333 !important;
 margin-left: -7px !important;
 padding-left: 10px !important;
}
#center_col ._je > b, #center_col ._je span b {
 color: #333333 !important;
}
#center_col ._p a {
 color: #3366cc !important;
}
.ac.ads-creative {
 color: #dc4437 !important;
}
}
