css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.pogdesign.co.uk/cat/)
{

/** HIDE
 */
.ep label,
.ep p a,
#popshow,
#pop,
#tagcloud,
#footinfo,
#footer
{
  display: none !important;
}
#data
{
  padding: 0 !important;
}
/** MENU MUTER
 */
.logo > ul:nth-child(4), /*.addshows*/
ul.features:nth-child(2) > li:nth-child(4), /*.new*/
ul.features:nth-child(2) > li:nth-child(3), /*.register*/
ul.features:nth-child(2) > li:nth-child(2), /*.login*/
ul.features:nth-child(3) > li:nth-child(4), /*.next*/
ul.features:nth-child(3) > li:nth-child(3) /*.renew*/
{
  display: none !important;
}
/** I LIKE SQUARES
 */
*
{
  border-radius: 0 !important;
}
/** BOX CRUSHER
 */
#month_box td p,
#month_box td div,
#month_box .ep p
{
  display: inline !important;
  font-size: 0 !important;
  line-height: 0 !important;
  height: auto !important;
  width: auto !important;
  padding: 0 !important;
  margin: 0 !important;
}
/** FIX PADDING
 */
#month_box td.day strong,
#month_box td.today strong
{
  padding: 5px 8px 2px 8px !important;
}
#month_box .ep p
{
  margin-left: 8px !important;
  margin-right: 8px !important;
  line-height: 20px !important;
}
#month_box .ep:last-child p
{
  margin-bottom: 8px !important;
}
#month_box .ep p a[rel*="q/"]
{
  font-size: 15px !important;
}
/** SHOW LIST (URLs are cAsE SeNsiTiVE)
 */
#month_box .ep p a[href*="cat/24"],
#month_box .ep p a[href*="cat/Almost-Human"],
#month_box .ep p a[href*="cat/Boardwalk-Empire"],
#month_box .ep p a[href*="cat/Community"],
#month_box .ep p a[href*="cat/Game-of-Thrones"],
#month_box .ep p a[href*="cat/Homeland"],
#month_box .ep p a[href*="cat/Justified"],
#month_box .ep p a[href*="cat/Louie"],
#month_box .ep p a[href*="cat/Mad-Men"],
#month_box .ep p a[href*="cat/Modern-Family"],
#month_box .ep p a[href*="cat/New-Girl"],
#month_box .ep p a[href*="cat/Parks-and-Recreation"],
#month_box .ep p a[href*="cat/QI"],
#month_box .ep p a[href*="cat/Sherlock"],
#month_box .ep p a[href*="cat/The-Sopranos"], /*RIP*/
#month_box .ep p a[href*="cat/The-Walking-Dead"],
#month_box .ep p a[href*="cat/True-Detective"]
{
  font-size: 12px !important;
  display: inline !important;
}
/** HIDE SEASON/EP WHICH IS SHOW BY DEFAULT
 */
#month_box .ep p a[href*="/Season-"]
{
  display: none !important;
}

/** THE END
 */
}
