css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tomshardware.com"),
domain("tomshardware.de"),
domain("tomshw.it"),
domain("tomsguide.com"),
domain("tomsguide.fr"),
domain("tomshardware.co.uk"),
domain("bestofmedia.com"),
domain("thg.ru") {

.body-section
{
background: #000000 !important;
}
.title-underline, .bgg
{
background: #222222 !important;
}
.header-tags, .summary-dropdown, th, .author-bio-name
{
background-color: #BE3806 !important;
}
.page-content, .header-content, .most-popular,
.share-bar, td, .home-page .page,
.home-page .home-tsb,
.listing-item a
{
background-color: #222222 !important;
}
#incontent-thread-creation, .creation-infos .author,
.bbcode .quote
{
background-color: #BE3806 !important;
}
#incontent-thread-creation
{
border: 1px #FF0000 solid !important;
}
.home-page .home-tsb
{
border: 1px #BE3806 solid !important;
}
.creation-infos .author, .bbcode .quote
{
border-left: 5px #FFFFFF solid !important;
}
.page
{
border-left: 1px #333333 solid !important;
}
.page
{
border-right: 1px #333333 solid !important;
}
.most-popular li, .listing-item a
{
border-bottom: 1px #BE3806 solid !important;
}
.page-content-rightcol
{
border-color: #BE3806 !important;
}
.page, .header-tags .header-tags-more .listing > li:hover
{
box-shadow: none !important;
}
.author-bio-name
{
padding-left: 3px !important;
}
.author-bio-name
{
padding-right: 3px !important;
}
.header-tags .header-tags-more .listing > li:hover
{
text-shadow: none !important;
}
a, p, th, td, h1, h2, h3, h4, label, time, span, blockquote, 
.thread__content, .thread__title, .summary-listing-title,
.creation-infos .author, .answer__content, .header-content,
.said, .quote, .quick-edit, .toggle-expand-content,
.author-bio-name, .rc-item .rc-headline
{
color: #FFFFFF !important;
}
}
