css:
@-moz-document domain("www.hitbox.tv") {
/*******************************BROWSE MENU ******************************/
.subNav.ng-scope
{
background-color: black !important;
}

h7.active
{
color: white !important;
border-color: darkred !important;
}
h7
{
color: white !important;
}

H4.ng-binding, .subNav .title
{
border-bottom: 1px solid white !important;
color: white !important;
}
.tViewers.icon-eye.ng-binding
{
color: lightgreen !important;
position: relative !important;
}

img.ng-scope
{
border: 1px solid #1f1f1f!important;
}

/*************************************************************************/


/*******************************Stream Page Background ******************************/
.meta, .userContent.cf, .ng-pristine.ng-valid, form[name=signupForm] input
{
background: none !important;
background-color: #000 !important;
}
.status.ng-binding, .game.ng-binding, .hover, label, .detail .meta .name, .tStatus.ng-binding,
.tMetaSmall.ng-binding, .ng-binding > p
{
color: white !important;
}

.tTitle
{
color: darkred !important;
}

.hover.active
{
color: white !important;
border-bottom: 6px solid darkred !important;
}

.link, .embed
{
border: none !important;
}

.followBubble.ng-binding
{
color: white !important;
}

.detailInner, .userContent, .meta
{
background-color: #000 !important;
border-bottom: 1px solid grey !important;
}

.viewers.fltR.icon-eye.ng-binding
{
color: white !important;
background: black !important;
}

BUTTON.icon-ok.following.btnBlueHover.ng-binding, .followBubble.ng-binding
{
color: white !important;
}

/***********************END Stream Page Background ******************************/


/*********************************** CHAT ********************************/
[onmouseover="this.style.borderColor='#2E2E2E'"], [onmouseover="this.style.borderColor='#585858'"]
{
color:#00FF00 !important;
} 

[onmouseover="this.style.borderColor='#848484'"], [onmouseover="this.style.borderColor='#0404B4'"]
{
color:#ffd39b !important;
}

[onmouseover="this.style.borderColor='#5F04B4'"], [onmouseover="this.style.borderColor='#5858FA'"]
{
color:#00eeee !important;
}

[onmouseover="this.style.borderColor='#0B610B'"], [onmouseover="this.style.borderColor='#045FB4'"]
{
color:#87CEEB !important;
}

[onmouseover="this.style.borderColor='#0404B4'"], [onmouseover="this.style.borderColor='#0000FF'"]
{
color:#eec900 !important;
}

.icon-cog.hoverG2
{
color: white !important;
}

.chatInput.ng-scope
{
border-top: 1px solid grey !important;
background-color: black !important;
}

.chatBody.ng-scope.fontSize1, li
{
background-color: #000 !important;
}

.chatBody.ng-scope.fontSize1 .user span[ng-if="message.name"]
{
  background-image: url('http://www-cdn.jtvnw.net/images/xarth/g/g18_sword-FFFFFF80.png');
  background-repeat: no-repeat;
  padding-left: 18px;
}

SPAN.message.ng-binding
{
color: #D8D8D8 !important;
}

.bufferTimestamp, .ng-binding
{
background-color: #000 !important;
border: none !important;
}

.icon-upload, .icon-upload:hover
{
  color: #fff;
  cursor: pointer;
}

.textareaWrap
{
background-color: #000 !important;
border: none !important;
}
[placeholder]
{
color: white !important;
}
/*************************************************************************/

/******************** Search Menu ****************/
.head
{
color: white !important;
}
/*************************************************/

/******************** Home Menu ****************/
.homeContent
{
background-color: black !important;
}

.homeContent section, .homeFeaturedCarousel
{
background-color: black !important;
background-image: none !important;
}

/*********************************************/

/******************** Game Menu ****************/
.pageContent.gW, .scrollDiv.bgWhite.ng-scope.ps-container
{
background-color: black !important;
}

H1.ng-binding
{
color: white !important;
}
/***********************************************/

/*******************Settings Menu******************/
UL.chatBody.ng-scope.fontSize0, UL.chatBody.ng-scope.fontSize1, UL.chatBody.ng-scope.fontSize2, UL.chatBody.ng-scope.fontSize3, UL.chatBody.ng-scope.fontSize4
{
background-color: black !important;
}

.chatUsers.ng-scope.ps-container.fontSize1, .chatUsers.ng-scope.ps-container.fontSize0, .chatUsers.ng-scope.ps-container.fontSize2, .chatUsers.ng-scope.ps-container.fontSize3, .chatUsers.ng-scope.ps-container.fontSize4
{
background-color: black !important;
}

.size0.hoverG2bg, .size1.hoverG2bg, .size2.hoverG2bg, .size3.hoverG2bg,.size4.hoverG2bg
{
background-color:#3f3f3f !important;
}

A.ng-binding
{
color: lightgreen !important;
}

h5
{
color: white !important;
}

DIV.chatSettings.nono.ui-show, .chatSettings.nono
{
background-color: black !important;
}

.fontSize, .icon-popup.hoverG2, .icon-cancel.hoverG2
{
color: white !important;
}

/*************************************/
}
