@-moz-document regexp("https?://twitter.com/.*") {
body{font:14px/20px Roboto,sans-serif;color:#333}

.global-nav , .global-nav-inner {
  border:0;
background: #00ACEE;
height: 60px;
}
.global-nav .container {
  padding: 9px 0;
  text-align: left;
 }
    
.bird-topbar-etched,.pushstate-spinner {
color: #fff;
font-size: 30px;
margin-top: 6px;
margin-left: 20px;
}

.logged-in .topbar {
width: auto;
padding-left: 165px;
}



.logged-in .topbar .global-nav [role=navigation] {
display: block!important;
position: fixed;
top: 0;
left: 0;
width: 165px;
height:100%;
background:#213246;
}
.nav {
margin: 0;
}

#global-actions>li {
height: 50px;
overflow: hidden;
  width: 100%;
}
.nav>li>a, .nav>li>button {
height: 50px;
  }
#global-actions>li:hover>a, #global-actions>li.active>a {
height: 46px;
}

.logged-in .wrapper, .logged-in .wrapper-narrow, .logged-in .wrapper-permalink {
padding: 59px 0 0;
width: 100%;
padding-left: 165px;
float: right;
  box-sizing: border-box;
  min-width: 855px;
  background: #F5F6F8!important;
}
.dashboard {
float: right;
width: 25%;
}
.content-main {
float: left;
width: 75%;
}
.content-header, .content-no-header {
border: 0;
border-radius: 0;
}
.content-header .header-inner, .content-no-header .no-header-inner {
padding: 18px 24px;
  min-height: 60px;
border-radius: 0;
  box-sizing:border-box;
}

.stream .stream-items{
padding: 3%;
  display: flex;
  flex-flow: row wrap;
  justify-content: space-between;
  align-items: flex-start;

}
.stream-items> li.stream-item {
box-sizing: border-box;
width: 48.5%;
margin-bottom: 3%;
float: left;
}

.stream-items> li.stream-item.open {
width: 100%;
}
.stream-items> li.separation-module {
box-sizing: border-box;
width: 100%;
}
.stream-items> li.semi-expanded {
box-sizing: border-box;
width: 100%;
}
#stream-items-id > li.stream-media-items.stream-item.separation-module > div, .media-grid ,.media-row-content.photo-list{
display: flex;
flex-wrap: wrap;
}
.content-main .stream-container .media-thumbnail.grid-tweet,.media-row-content.photo-list .media-thumbnail {
clear: none;
flex: 1 1 auto;
margin: 4px;
}
.media-box img {
height: auto!important;
width: 100%!important;
        }
.content-main .stream-container .media-thumbnail.grid-tweet:last-child {
flex: 0 1 auto;
}
.content-main .grid-tweet[scaled-width="574"]{
  width: 100%!important;
      }
.tweet-actions .with-icn b {
display:none
}

.js-stream-item.stream-item, .unfocusable-stream-item {
background: #fff;
border: 1px solid #EAECEB;
border-radius: 6px;
}
.tweet {
padding: 18px 20px;
}

div.stream-item-header {
border-bottom: 1px solid #EAECEB;
  margin-bottom: 15px;}

.tweet .time {
float:right;
}
.stream-item .content, .permalink-tweet .content {
margin-left: 0; 
}
.stream-item-header .avatar, .permalink-header .avatar {
 float: none; 
margin-top: 3px;
 margin-left: 0; 
}

.with-media-forward .cards-base .media-forward {
border-radius: 0;
max-height: 300px;
}
.content > .cards-media-container{
margin: 0 -20px;
}
.tweet div.content > .stream-item-header > a {
overflow: auto;
display: block;
height:60px;
padding-left: 60px;
}
.tweet div.content > .stream-item-header > a *{
float: left;
}
.tweet div.content > .stream-item-header > a .username{
clear:left;
}

.stream-item-header .avatar, .permalink-header .avatar {
position: absolute;
left: 20px;
top: 18px;
}
.tweet {
  color: #AAA;
}
.tweet .time {
position: absolute;
right: 20px;
top: 18px;
}
.avatar {
border-radius: 3px;
}




.content-main {
border-right:solid 1px #E2E3E5 ;
box-sizing:border-box;
}

.module .flex-module {
background-color: inherit; 
border: 1px solid #E2E3E5;
border-width: 1px 0 0;
padding: 8%; 
border-radius:0!important;
}
.flex-module-header {
margin-bottom: 8%;
padding-bottom: 8%;
border-bottom: 1px solid #E2E3E5;
}



.Icon--mediaplay:before {
height: 60px;
line-height: 50px;
}



#doc.route-home .module .tweet-box{
display:none
}

.content-main .stream-container .media-thumbnail.grid-tweet {
height: 15em!important;
}
.content-main .stream-container .media-thumbnail.grid-tweet img {
min-height: 100%;
position: relative;
height: auto;
width: 100%;
min-width: 100%;
display: block;
  margin:0!important;
}
.profile-card.profile-header .profile-picture {
border: 0;
box-shadow: none;
}
.profile-card.profile-header .profile-picture, .profile-card.profile-header .profile-picture .avatar {
width: 80px;
height: 80px;
border-radius: 100%;
}

