css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("drafts.in") {
.pull-right{
    background-image: url(http://i.imgur.com/2EAq822.png);
    background-position: left bottom;
    box-shadow: 5px 5px 4px rgba(0,0,0,0.2);
    padding: 6px;
}
@font-face {
  font-family: 'Crimson Text';
  font-style: normal;
  font-weight: 400;
  src: local('Crimson Text'), local('CrimsonText-Roman'), url(http://themes.googleusercontent.com/static/fonts/crimsontext/v4/3IFMwfRa07i-auYR-B-zNXhCUOGz7vYGh680lGh-uXM.woff) format('woff');
}
h1{
    text-transform: capitalize;
    background-image: url(http://i.imgur.com/lDLdwrN.png);
    background-repeat: no-repeat;
    margin-left: 10px;
    padding: 10px 20px;

}
body{
    background-image: url(http://i.imgur.com/Fh9HfbJ.png);
    font-family: 'Crimson Text', serif;
}
.container{
    border: none !important;
}
.container img{
    border: 1px solid black;
    border-radius: 15px;
    margin: 4px;
    box-shadow: 0px 2px 4px rgba(0,0,0,0.5);
}
}
