css:
/* By SimonTesla - https://www.weasyl.com/~simon */
/* Version 0.2 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
    url-prefix("https://www.weasyl.com/messages/submissions"),
    url-prefix("https://www.weasyl.com/search"),
    url-prefix("https://www.weasyl.com/submissions")
{
    .grid-item 
    {
        width: 10rem !important;
        padding-top: 2px !important;
        
    }
    .grid-item .ol-container {
        /* overflow: visible; */
        padding-bottom: 4rem !important;
        /*width: 125% !important;*/
    }
    .grid-item .ol,
    .grid-item .hover .ol
    {
        top: 120px !important;
        left: 0px !important;
        background-color: transparent !important;
        color: rgb(77, 77, 77) !important;
        text-align: left !important;
    }
    
    #search-stage .grid-item .ol,
    #submissions-stage .grid-item .ol
    {
        color: rgb(230, 230, 230) !important;
    }
    
    .grid-item.checkbox > label
    {
        padding: 0 !important;
    }
    
    .grid-item a
    {
        margin: 0 !important;
        width: 100% !important;
    }
    
    .grid-item .ol h5,
    .grid-item .ol h6
    {
        border: 0 !important;
        margin-left: 0 !important;
        padding-left: 0 !important;
    }
    
    .grid-item .thumbnail .rating
    {
        bottom: auto !important;
        right: auto !important;
        left: -4px !important;
        top: -4px !important;
        border-width: 2px 0 0 2px !important;
    }
    
    .grid-item .thumbnail
    {
        border: 0 !important;
    }
    
    .grid-item.checkbox > label input[type="checkbox"]
    {
        margin: 0 0 .5rem 0 !important;
    }
}
