css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://forum.arduino.cc/index.php?topic') {
#wrapper{
min-width: 500px!important;
width: 900px!important;
}

.profile .memberPostGroup, .profile .memberPosts, .profile .memberKarma {
margin-top: 2px!important;
}

.windowbg2 table div.post img{
max-width:100%!important;
}
}

@-moz-document url-prefix('http://forum.arduino.cc/index.php?board') {
#wrapper{
min-width: 900px!important;
width: 90%!important;
}
}


@-moz-document url-prefix('http://forum.arduino.cc/') {
#pageheader{
text-align:center!important;
margin-bottom: 0!important;
}

#pageheader > *{
max-width: 900px!important;
margin: 0 auto!important;
}

body {
line-height: 105%!important;
background-position: 0 -86px!important;
}

#navWrapper, .navWrapper.row, .top-bar, nav.top-bar ul.left, nav.top-bar ul.right, .top-bar-section li a:not(.button), .top-bar-section ul li.active {
height: 29px !important;
}

body *{
line-height: inherit!important;
}

.catbg, .catbgf, .catbgf-m, .catbgnew,
#navBreadCrumb,
#pageheader{
padding-top: 0!important;
padding-bottom: 1px!important;
}

/*.windowbg-l, .windowbg2-l, .windowbg, .windowbg2, .windowbg3*/
td:not([align="center"]){
padding:0 3px!important;
}

td[align="center"],
#forumActions ul.button-group li a.small.button,
input[type="button"] {
padding:0 8px!important;
}

select, input[type="button"]{
height: 20px!important;
}


select > option[disabled="disabled"]{
line-height: 5px!important;
height: auto!important;
margin:0 0 -10px 0!important;
}

select > option:not([disabled="disabled"]){
line-height: 98%!important;
}

.windowbg2 > br, 
.windowbg3 > br,
.msgBoardPostDate > br{
display:none!important;
}

.windowbg2 > .smalltext{
display:inline-block!important;
}

td[valign="top"]{
vertical-align: middle!important;
}

.windowbg-l img, .windowbg2-l img, .windowbg2 img{
width: 15px!important;
height: 15px!important;
}

#userPosts{
margin-top: 0!important;
margin-bottom: 0!important;
}

#userPosts{
height: auto!important;
}

.windowbg3 > *:not([align="right"]),
.windowbg > *:not([align="right"]){
padding: 0 4px!important;
clear:none!important;
}

a[id^="newicon"]{
float:left!important;
}

.windowbg3 > b > span, 
.windowbg > span{
display:inline!important;
max-width:95%!important;
}

.windowbg3 > b > span > a{
display:inherit!important;
}
}
