css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://translate\\.google\\.((com?\\.)?[a-z]{2}|com|cat)/.*") {
    @media all and (max-width: 600px) {
      html {overflow-y:scroll!important;}
      #gb,#gba,#gt-appbar,#gt-ft, #gt-bbar-c, #gt-ft-res {display: none !important;}
      #gt-text-all {padding:5px !important;}
      #gt-c {min-width: 0 !important;}
      #gt-lang-left, #gt-lang-right, #gt-res-c, #gt-src-c, .gt-cc-l, .gt-cc-r {
        float:none !important;
        position: relative;
        top: 10px;
        width: auto !important;
      }
      /* Vertical languages lists By banthaz from userstyles.org*/
      .goog-menu.goog-menu-vertical {
        overflow-y:auto!important;
        overflow-x:hidden!important;
      }
      .goog-menu.goog-menu-vertical td {display:block!important;}
      /* Vertical Dictionary By banthaz from userstyles.org*/
      #gt-res-dict td {display:inline-block!important;}
      .gt-baf-cell.gt-baf-translations {
        height:auto!important;
        white-space:normal!important;
      }
      #gt-res-dict {
        width:-moz-calc(100% - 15px)!important;
        overflow:visible!important;
      }
      .gt-baf-cts {float:left!important;}
      .gt-baf-cts::after {
        content:""!important;
        display:block!important;
        height:inherit!important;
        width:24px!important;
        background:rgba(0,0,0,.2)!important;
      }
  }
}
