css:
@namespace url(http://www.w3.org/1999/xhtml);

	/*
	Section 1 TLD ...change this to your favourite phpbb V2 board
		This should suit most V2 boards
		You'll probably need to tweak the sub-section...
		  main navigation menu

	Section 2 url-prefix & url
		implement row hover for certain pages
		Again, this should suit most V2 boards

	Section 3 TLD ...some ibdof site tweaks
		IMPORTANT: these may need adjusting for your site

	Section 4 url-prefix
		extra styling for the "Goto Page" line below the topic title on certain pages
		Again, this should suit most V2 boards

	*/


/*^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^*/
/*	Begins Section 1 TLD

	Colors inspired by "ExtremeDarkRed" style
	  though I opted for gold highlites
*/
@-moz-document domain("rpbastion.forumotion.com") {

body {
/* To have a solid color, uncomment the greyed out commented area below, denoted by /* & */  
/*	background: #00050A !important; */
	margin: -25px !important;
	padding: 25px !important;
}
body, font,
th, td, p {
	font: 12px Verdana, Arial, Helvetica, sans-serif !important;
}
hr {
	/* To have a Transparent color: Change the Border-Color: to #000 */
	border-color: #0A0A33 !important;
}
	
a:link, a:active {
	color: #662900 !important;
}
a:link, a:visited {
	color: #FF6600 !important;
}
a:hover {
	color: #009999 !important;
	text-decoration: underline !important;
}
.forumline, table.thHead {
	background: #000 !important;
	border: 1px #000 solid !important;
}

td.bodyline {
	background:#000 !important;
}

td.row1, td.row2, 
td.row3, td.row3Right {
	background: #00000A !important;
	border: 0px #000 solid !important;
	border-width: 1px 1px 0px 0px !important;
}
td.row2 {
	background: #000A14 !important;
}
td.spaceRow {
	background: #001F1F !important;
	border-color: #000 !important;
	border-width: 1px 050px 1px 155px !important;
}
th, 
th.thHead, th.thSides,
th.thLeft, th.thRight,
th.thBottom, th.thTop,
th.thCornerL, th.thCornerR,
td.thTop {
	color: #333385 !important;
	font-weight: bold !important;
	font-size: 13px !important;
	height: 21px !important;
	border: 0px #000 solid !important;
	border-width: 0px 1px 1.5px 1px !important;
	background: #000A0A url(http://s5.postimg.org/h6f8uizhj/alexiuss_weapon_gun_city_buildings_torn.jpg) !important;
}
th.thHead {
	font-size: 15px !important;
	height: 25px !important;
}

td.cat,
td.catHead, td.catBottom, 
td.catSides, 
td.catLeft, td.catRight, 
td.rowpic {
	color: #fff !important;
	font-weight: bold !important;
	font-size: 20px !important;
	height: 22px !important;
	border: 0px #000 solid !important;
	border-width: 0px 10px 0px 0px !important;
	background-color: #000 !important;
	background-repeat: repeat !important;
	/* For an alternate picture: Swap the below image link with this one (http://s5.postimg.org/5vuyv2y7b/Raven_Mage_Dark_Ice_Flow.png) */
	background-image: url(http://s5.postimg.org/puz6gagmf/midkemia_by_helmuttt.jpg) !important; 
}

.maintitle, h1, h2 {
	font: bold 0px "Trebuchet MS", Verdana, Arial, Helvetica, sans-serif !important;
	color: #8F2400 !important;
	line-height: 120% !important;
}
	
.gen,
.genmed,
.gensmall {
	color:#7A9999 !important;
}
.gen {
	font-size: 12px !important;
}
.genmed {
	font-size: 11px !important;
}
.gensmall {
	font-size: 10px !important;
}
a.gen,
a.genmed,
a.gensmall {
	color: #CC9933 !important;
	text-decoration: none !important;
}
a.gen:hover,
a.genmed:hover,
a.gensmall:hover {
	color: #6BB28F !important;
	text-decoration: underline !important;
}
	
.cattitle {
	color: #A3A3CC !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
a.cattitle, a.cattitle:visited {
	color: #6B6BB2 !important;
	text-decoration: none !important;
}
a.cattitle:hover {
	color: #7A7A99 !important;
	text-decoration: underline !important;
}
	
.forumlink {
	color: #CC9933 !important;
	font-weight: bold !important;
	font-size: 12px !important;
}
a.forumlink {
	color: #3D3D99 !important;
	text-decoration: none !important;
}
a.forumlink:hover {
	color: #ff0000 !important;
	text-decoration: underline !important;
}
	
.nav {
	color: #3D3D99 !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
a.nav,
a.nav:visited {
	color: #246BB2 !important;
	text-decoration: none !important;
}
a.nav:hover {
	color: #6B6BB2 !important;
	text-decoration: underline !important;
}
	
.topictitle {
	color: #3399FF !important;
	font-size: 11px !important;
	font-weight: bold !important;
}
a.topictitle:link,
a.topictitle:visited {
	color: #CC9933 !important;
	text-decoration: none !important;
}
a.topictitle:hover {
	color: #ff0000 !important;
	text-decoration: underline !important;
}
	
.name {
	color: #fff !important;
	font-size: 11px !important;
}
.postdetails {
	color: #3399FF !important;
	font-size: 10px !important;
}
	/* HERE LIES THE POST FIELD COLOR */
.postbody {
	color: #476BB2 !important;
	font-size: 12px !important;
}
a.postlink:link,
a.postlink:visited {
	color: #526666 !important;
	text-decoration: none !important;
}
a.postlink:hover {
	color: #B82E00 !important;
	text-decoration: underline !important;
}
	
.code {
	color: #00D900 !important;
	background: #000 !important;
	font: 11px Courier, "Courier New", sans-serif !important;
}
.quote {
	color: #fff !important;
	background: #000 !important;
	font: 11px Verdana, Arial, Helvetica, sans-serif !important;
}
	   


.copyright {
	color: #fff !important;
	font: 10px Verdana, Arial, Helvetica, sans-serif !important;
	letter-spacing: -1px !important;
}
a.copyright,
a.copyright:visited {
	color: #CC9933 !important;
	text-decoration: none !important;
}
a.copyright:hover {
	color: #ff0000 !important;
	text-decoration: underline !important;
}
	/* HERE LIES THE TEXT FIELD FOR QUICK POST */
input,
textarea,
select {
	color: #CC9933 !important;
	font: 9px Verdana, Arial, Helvetica, sans-serif !important;
	border-color: #D65930 !important;
	background-color: #140000 !important;
        -moz-appearance: none;
}
input {
	text-indent: -1px !important;
}
select,
input.post,
input.mainoption,
input.liteoption,
textarea.post {
	background-color: #000014 !important;
	border: 2px #0A000A solid !important;
}
input[type="button"] {
	background-color: #800000 !important;
	border: 1px #000 solid !important;
}
input[type="button"]:hover {
	background-color: #000 !important;
	border: 1px #ff0000 solid !important;
}
input.mainoption,
input.liteoption {
	font-weight: bold !important;
}
input.mainoption:hover,
input.liteoption:hover {
	background-color: #000 !important;
}
input.liteoption {
	font-weight: bold !important;
}
	
.helpline {
	background: inherit !important;
	color: #B0B0B0 !important;
	border-style: none !important;
}


	/**
		main navigation menu on top of the page
		  background gif full width of container cell
	*/
td.bodyline:first-child td[valign="top"][align="center"][colspan="2"] {
	height: 22px !important;
	white-space: nowrap !important;
	border: 1px #808080 solid !important;
	border-width: 1px 0px 1px 0px !important;
	font-size: 10px !important;
/*	background: #cc0000  url(http://s5.postimg.org/4efvrepwj/Emo_Cartoon_Hug.jpg) !important; */
}
.mainmenu {
	color: #ff0000 !important;
}
a.mainmenu:link,
a.mainmenu:visited {
	font-size: 10px !important;
	color: #669999 !important;
	text-decoration: none !important;
}
a.mainmenu:hover {
	color: #FF6262 !important;
	text-decoration: underline !important;
}

/*DON'T KNOW WHAT THESE DO - not used on ibdof */
.agreeoptions {
	font-size: 11px !important;
	color: #fff !important;
}
a.agreeoptions,
a.agreeoptions:visited {
	color: #fff !important;
	text-decoration: none !important;
}
a.agreeoptions:hover {
	color: #D0D0D0 !important;
	text-decoration: underline !important;
}

}
/* ends Section 1 TLD */


/*^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^*/
/*	Begins Section 2 - Site Prefix & url Rules

	implement row hover for specific pages
*/

/* Section not working 
@-moz-document url-prefix(http://www.rpbastion.forumotion.com/index), 
		url-prefix(http://www.rpbastion.forumotion.com/viewforum), 
		url-prefix(http://www.rpbastion.forumotion.com/favorites), 
		url-prefix(http://www.rpbastion.forumotion.com/watched_topics), {
  table.forumline tr:hover > td { background-color: #0D0D0D !important; }
}

 Section not working */
@-moz-document 	url(http://www.rpbastion.forumotion.com/privmsg.php?folder=inbox), 
		url(http://www.rpbastion.forumotion.com/privmsg.php?folder=sentbox), 
		url(http://www.rpbastion.forumotion.com/privmsg.php?folder=outbox), 
		url(http://www.rpbastion.forumotion.com/privmsg.php?folder=savebox) {
  table.forumline tr:hover > td { background-color: #0D0D0D !important; }
}
/* still doing row hover - strange!! only main-table on site not named "forumline" ??? */

/* ends Section 2 */



/*^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^*/
/*	Begins Section 3 TLD

	some IBDoF tweaking/overrides to the default styling
*/
@-moz-document domain("rpbastion.forumotion.com") {
/* /Start Ghosty Note\ Might not be fully implemented /End Ghosty Note\ */
/* site graphic - copy of original modified to have a transparent background */
img[src*="http://illiweb.com/fa/empty.gif"] {
	width: 0px !important;
	height:0px !important;
    background-image: url(http://s5.postimg.org/4efvrepwj/Emo_Cartoon_Hug.jpg) !important; 
	padding-left:0px;
	padding-top:0px;
	margin-bottom: 0px !important;
}

/*site title font*/
/*td.topbkg + td > span.maintitle*/
td.bodyline > table[cellpadding="0"][cellspacing="0"] span.maintitle {
	font-family: Georgia, serif !important;
}

/* main navigation menu - some additional tweaks */
span.mainmenu a:link,
span.mainmenu a:visited {
	font-size: 10px !important;
	color: #fff !important;
	text-decoration: none !important;
}
span.mainmenu a > b > font {
	font-weight: normal !important;
	font-size: inherit !important;
	color: inherit !important;
}
span.mainmenu a:hover {
	color: #D0D0D0 !important;
	text-decoration: underline !important;
}

.quote {
	line-height: 125% !important;
	color: #669999 !important;
}

.code {
	font-family: Calibri !important;
	font-size: 12px !important;
	font-weight: bold !important;
}

/*index.php - fora title*/
.cattitle {
	padding-left: 0.51em !important;
	font-size: 13px !important;
}

/*index.php - fora description*/
span.forumlink + span.genmed {
	padding: 0.5em !important;
	line-height: 140% !important;
	color: #f2f2f2 !important;
	display: block !important;
}

/*viewtopic postings - a bit of extra spacing around the post & between lines*/
.postbody {
	padding: 0 0.5em !important;
	line-height: 140% !important;
	display: block !important;
}
/*posting.php*/
textarea.post {
	padding: 0.5em !important;
	display: block !important;
}
/*override visited fora link color*/
a.topictitle:visited {
	color: #1F1F7A !important;
}
a.topictitle:hover {
	color: #ff0000 !important;
	text-decoration: underline !important;
}

}
/* ends Section 3 TLD */


/*^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^*/
/*	Begins Section 4 - Site Prefix Rules

	viewforum.php & links.php - Goto Page
	search.php & daily.php - Goto Page
	  extra spacing and try to make the cell look less cluttered
	  by fading these lines a bit more into the background
*/
@-moz-document  url-prefix(http://www.rpbastion.forumotion.com/viewforum), 
		url-prefix(http://www.rpbastion.forumotion.com/links), 
		url-prefix(http://www.rpbastion.forumotion.com/getdaily), 
		url-prefix(http://www.rpbastion.forumotion.com/search) {

  span.topictitle + span.gensmall,
  td.row2 > span.gensmall {
	margin-top: -0.5em !important;
	padding-left: 1em !important;
	display: block !important;
	color: #808080 !important;
  }

  td.row2 > span.gensmall {
	margin-top: 0 !important;
	padding-top: 0.5em !important;
  }

  span.topictitle + span.gensmall img,
  td.row2 > span.gensmall img {
	opacity: 0.5 !important;
  }

  span.topictitle + span.gensmall a:link,
  span.topictitle + span.gensmall a:visited,
  td.row2 > span.gensmall a:link,
  td.row2 > span.gensmall a:visited {
	color: #9C8049 !important;
  }

  span.topictitle + span.gensmall a:hover,
  td.row2 > span.gensmall a:hover {
	color: #ff0000 !important;
  }
}
/* ends Section 4 */


/*^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^*/
