css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://diit.cz),
               url-prefix(http://cdr.cz)
{

body, p, .field-item, .node-small-teaser h2 {
    font-size: 14px !important;
    font-family: sans-serif !important;
    line-height: 1.2em !important;
}

.node-teaser .image-wrapper .field-item img, .node-teaser .image-wrapper {
    height: 180px !important;
    width: 268px !important;
}

.node-teaser {
    height: 180px !important;
    margin-bottom: 5px !important;
}

.node-small-teaser, .node-small-teaser img {
    height: 65px !important;
    margin-bottom: 5px !important;
}

.node-small-teaser .image-wrapper {
    height: 65px !important;
    width: 80px !important;
}

.node-teaser h2 {
    font-size: 20px !important;
    font-family: sans-serif !important;
    line-height: 1.2em !important;
}

.region-top, .komercni-sdeleni, .quicktabs-tabs {
    display: none;
}

ul.block-links li a {
    font-size: 14px !important;
}

.indented {
    border-left: 1px solid rgb(204, 204, 204);
}

.comment {
    padding-left: 10px;
    padding-top: 15px;
    margin-bottom: 0px !important;
}

.quicktabs-wrapper .item-list {
    padding-bottom: 0px !important;        
}

}
