css:
@-moz-document domain("hitbox.tv") {
div.chatUsers{
	background:rgb(16,16,16)!important;
}

div.chatUsers > div.ng-scope > ul > li > a{
	color:rgb(202,202,202)!important;
}

div.chatUsers > div.ng-scope > ul > li > a:hover{
	color:rgb(100,202,100)!important;
}
span.icon-popup.hoverG2, span.icon-cancel.hoverG2 {
color: rgb(170, 145, 180);
}

/* Main Homepage */
div.homeContent{
	background:rgb(16, 16, 16)!important;
	color:rgb(252, 252, 252)!important;
}
span.icon-popup.hoverG2, span.icon-cancel.hoverG2 {
color: rgb(170, 145, 180);
}

form.form_enclosed.formL.ng-valid.ng-dirty{
	background:rgb(96, 96, 96)!important;
	color:rgb(252, 252, 252)!important;
}

/* Search slide out*/
.subNav .search{
	color:rgb(252, 252, 252)!important;
}

.subNav .search .head {
	color:rgb(256, 256, 256)!important;
}


/* Account settings */
.settingsContent .title{
	border-bottom: 1px solid rgb(0,0,0)!important;
}

div.settingsContent{
	background:rgb(16, 16, 16)!important;
	color:rgb(252, 252, 252)!important;
}

.active:after{
	border-color:transparent rgb(16,16,16)!important;
}

.homeContent section{
	border-top: 1px solid rgb(0, 0, 0)!important;
}

/* Footer */
.footer a{
	color:rgb(252, 252, 252)!important;
}

.footer a:hover{
	color:rgb(143, 191, 0)!important;
}
span.icon-popup.hoverG2, span.icon-cancel.hoverG2 {
color: rgb(170, 145, 180);
}
.subNav{
	color:rgb(252, 252, 252)!important;
	background:rgb(25, 25, 25)!important;
}

div.scroll.ng-scope.ps-container{
	color:rgb(252, 252, 252)!important;
	background:rgb(25, 25, 25)!important;
}


/* Stream page */
div.title > span.name.ng-scope.ng-binding,div.tName.ng-binding{
	color:rgb(68, 50, 102)!important;
}
.chatBody .name {
    font-weight: 500;
    border-bottom-width: 1px;

    border-bottom-style: dashed;
    border-bottom-color: transparent;
    text-shadow: 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A, 0 0 2px #0A0A0A;
    cursor: pointer;
    padding-right: .25em;
}
div.title > span.status.ng-binding,div.tStatus.ng-binding{
	color:rgb(192, 192, 192)!important;
}

.channel .pollWrap {
background-color: hsl(0, 0%, 13%);
border-bottom: 1px solid hsl(0,0%,10%);
width: 100%;
color: rgb(147, 101, 179);
overflow: hidden;
}
.chatBody .title, .chatBody .message {
display: inline;
color: rgba(170, 173, 172, 1);
vertical-align: top;
}

.channel .poll .votes {
z-index: 2;
position: absolute;
right: .625em;
bottom: 1.375em;
font-weight: 300;
color: rgb(147, 101, 179);
}

span.icon-popup.hoverG2, span.icon-cancel.hoverG2 {
color: rgb(170, 145, 180);
}
a {
text-decoration: none;
border: 0;
color: #BB98E7;
}


button.following {
background-color: #443266;
box-shadow: none;
text-shadow: none;
color: #CCCCCC;
background-image: none;
padding: 0 .5rem;
font-weight: 400;
}

.btnBlue, .btnBlue:hover, .btnBlueHover:hover, .btnHoverBlue:hover {
background-color: #443266 !important;
border-color: #443266 !important;
color: white !important;
}

.btnBlueHover:hover, .btnHoverBlue:hover {
background-color: #d2b7ec !important;
border-color: #d2b7ec !important;
color: white !important;
}


.detail .meta{
	color:rgb(252, 252, 252)!important;
	background:rgb(25, 25, 25)!important;
	border-bottom:1px solid rgb(0, 0, 0)!important;
	border-top:1px solid rgb(0, 0, 0)!important;
}

div.player.center{
	background:rgb(25, 25, 25)!important;
	border-bottom:none!important;
}

div.hover{
	color:rgb(68, 50, 102)!important;
}

div.hover.active{
	color:rgb(175, 129, 201)!important;
	border-bottom:6px solid rgb(196,196,196)!important;
}

div.hover:hover{
	color:rgb(175, 129, 201)!important;
}

div.detailInner,div.userContent{
	color:rgb(252, 252, 252)!important;
	background:rgb(16, 16, 16)!important;
}

section.ng-scope{
	color:rgb(252, 252, 252)!important;
}

h1,h2,h3,h4,h5,h6,h7{
	color:rgb(252, 252, 252)!important;
}

a.twitter-follow-button{
	color:rgb(152, 152, 152)!important;
}

a.twitter-follow-button:hover{
	color:rgb(63, 173, 240)!important;
}

div.link, div.embed{
	border:none!important;
}

h7.active{
	border-bottom:6px solid rgb(196,196,196)!important;
}

ul.chatBody{
	background:rgb(16, 16, 16)!important;
}

span.message.ng-binding{
	color:rgb(204, 204, 204)!important;
}

section.chatInput{
	color:rgb(252, 252, 252)!important;
	background:rgb(16, 16, 16)!important;
	border-top: 3px solid rgb(26, 26, 26)!important;
}

textarea#chatInput{
	color:rgb(226, 226, 226)!important;
}

div.bufferTimestamp{
	background:rgb(16, 16, 16)!important;
}


.chatSettings {
position: absolute;
z-index: 3;
bottom: -23em;
right: 1em;
background-color: hsla(276, 7%, 52%, 0.88);
padding: .75em .75em 0;
min-width: 14em;
opacity: 0;
box-shadow: 0 0 4em hsla(0,0%,0%,.5);
transition: all 0.3s ease;
-webkit-transform: all 0.3s ease;
}

	
span.name[style*="color: rgb(46, 46, 46);"],span.name[style*="color:#2E2E2E"]{
	color:rgb(200, 200, 200)!important;
}

span.name[style*="color: rgb(11, 97, 11);"],span.name[style*="color:#0B610B"]{
	color:rgb(22, 160, 22)!important;
}

li.admin{
	background:rgb(16, 16, 16)!important;
}

li.user{
	background:rgb(16, 16, 16)!important;
}

h1, h2, h3, h4, h5, h6, h7 {
color: rgb(128, 122, 122)!important;
}

.checkbox input[type=checkbox]:checked + label:before {
color: hsl(281, 74%, 45%);
}

ul.chatBody > *{
    font-family: 'Roboto', Helvetica, Arial, sans-serif !important;
    line-height:1.2em !important;
}

span.icon-popup.hoverG2, span.icon-cancel.hoverG2{
	color:rgb(80, 80, 80)!important;
}
span.icon-popup.hoverG2, span.icon-cancel.hoverG2 {
color: rgb(170, 145, 180);
}
span.icon-popup.hoverG2:hover, span.icon-cancel.hoverG2:hover{
	text-decoration:underline!important;
	line-height:1.00em!important;
}

form.form_enclosed.formL.ng-valid.ng-dirty{
	background-color:rgb(16,16,16)!important;
}
}
