css:
@-moz-document domain(championselect.net) {




body
{
  color: #eee;
}

.main-page
{
  background-color: #222;
  background-image: none;
}

#maincontent
{
  background-color: #444;
}

h1, h2, h3, h4, h5, h6
{
  color: #eee;
  text-shadow: 0px 0px 10px rgba(227, 177, 150, 0.5);
}

/* http://www.championselect.net/champ/ * */

.row0
{
  background-color: #333;
}

.row1
{
  background-color: #373737; 
}

.well
{
  background-color: #373737;
}

.footer
{
  background-color: #303030;
}

/* Compare Champions */
.modal
{
  background-color: #7f7f7f;
}

.table-striped tbody tr:nth-child(odd) td, .table-striped tbody tr:nth-child(odd) th
{
  background-color: #999;
}

/* http://www.championselect.net/site/index */
.homeInfo
{
  background-color: #373737;
}

/* http://www.championselect.net/teamBuilder/index */
#list_picks li, #list_team1 li, #list_team2 li
{
  background-color: #444 !important;
}

#team1, #team2
{
  color: #4488BB !important; 
}


/* http://www.championselect.net/streams */

#livestream a:nth-child(odd)
{
  background-color: #6f6f6f;
}

#livestream a:nth-child(even)
{
  background-color: #666; 
}



/* Disqus */

.default body.dark .post-content header, .default body.dark .post-content header a, .default body.dark .post-content footer a, .default body.dark .post-content footer .voting
{
  color: #ccc;
}

.default .post-content header
{
  color: #999; 
}

.default .post-content header .time-ago, .default .post-content header .parent-link
{
  color: #9AADB6;
}

.default .post-content footer
{
  color: #81979C;
}

.default .post-content footer a
{
  color: #7F929C;
}

.postbody footer menu
{
  color: #ddd;
}

a
{
  color: #7F929C;
}






}
