css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {


/* ORIGINAL POST OR COMMENT */
blockquote[style="border-left:#000040 2px solid;margin-left:0px;margin-right:0px;padding-left:15px;padding-right:0px"]:first-of-type {
    color: rgba(0, 0, 0, 0.5) !important;
    font-size: .8em !important;
    overflow: auto;
    max-height: 200px;
    }
    
/* COMMENT */
blockquote[style="border-left:#000040 2px solid;margin-left:0px;margin-right:0px;padding-left:15px;padding-right:0px"]:last-of-type {
    color: rgba(0, 0, 0, 1.0) !important;
    font-size: 1em !important;
    max-height: none;
    }

/* "FROM HERE, YOU CAN" */
.adL {
    font-size: .7em !important;
    }

/* REPLY BY EMAIL */
form[action="http://www.livejournal.com/talkpost_do.bml"] {
    display: none !important;
    }

/* FOOTER */
.adL .im {
    display: none !important;
    }


}
