css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("longecity.org") {

/* Very Top Bar*/
#header_bar {
    background-color:#000000 !important;
}
/*Background behind "Longecity" logo*/
#branding{
	background:#000000 /*url(http://www.longecity.org/forum/public/style_images/master/branding_bg.png) repeat-x */ !important; 
	border-bottom:1px solid #4f4f4f !important;
	min-height:64px
}
/*side bars */
html,body{
	background-color:#151515 !important;
	color:#ffffdb !important;
}

/*Link text color*/
a{
	color:#ffffcc !important;
	text-decoration:none
}

/*mouseover link text color*/
a:hover{
	color:#328586
}

/*semitop bar*/
#primary_nav{
	background:#151515 !important;
	font-size:13px;
	padding:4px
0 0 0
}

/*Actual post background*/
.post_block {
    background: none repeat scroll 0% 0% #101010 !important;
    border-bottom: 1px solid rgb(214, 226, 235);
}


/*Actual post Text color*/
.post_body .post {
    color: #ffffdb !important;
}

/*Post headings (in threads) */
.post_block h3 {
    background: none repeat scroll 0% 0% #292929 !important;
    padding: 0px 10px;
    height: 36px;
    line-height: 36px;
    font-weight: normal;
    font-size: 16px;
    box-shadow: 0px 1px 0px #877d00 inset !important;
}

/*Quote Buttons*/
.ipsButton_secondary {
    height: 22px;
    line-height: 22px;
    font-size: 12px;
    padding: 0px 10px;
    background: #414141 !important;
    border: 1px solid rgb(219, 219, 219);
    box-shadow: 0px 1px 0px rgb(255, 255, 255) inset, 0px 1px 0px rgba(0, 0, 0, 0.3);  /* IF YOU WANT TO CHANGE QUOTE BUTTON OUTLINES */
    border-radius: 3px;
    color: rgb(97, 97, 97);
    display: inline-block;
    white-space: nowrap;
    transition: all 0.2s ease-in-out 0s;
}

/* First layer internal*/
#content {
    background-color: #0e0e0e !important;
}

/* Section Titles */
.maintitle {
    background: #434343 !important;
    padding: 10px 10px 11px;
    font-size: 16px;
    font-weight: 300;
    border-radius: 4px 4px 0px 0px;
    box-shadow: 0px 1px 0px #877d00 inset !important;
    border-width: 1px 1px 0px;
    border-color: #636243 !important;
    border-style: solid;
}

/*First layer inside Section, acts as pad, thin*/
.ipsBox {
    background: none repeat scroll 0% 0%  #0e0e0e !important;
}

/* VERY Thin line around objects in box*/
.ipsBox_container {
    border: 1px solid #151515 !important;
}

/*innermost part of Section containers*/
table.ipb_table {
    line-height: 1.3;
    border-collapse: collapse;
}
.unread {
    background-color: #131311 !important;
}

table.ipb_table td {
    padding: 10px;
    border-bottom: 1px solid #151515 !important;
}

.unread .altrow, .unread.altrow {
    background-color: #060606 !important;
}


/* Quoted Text*/
div.blockquote {
    font-size: 12px;
    padding: 10px;
    border-left: 2px solid #4c4c3d !important;
    border-right: 2px solid #4c4c3d !important;
    border-bottom: 2px solid #4c4c3d !important;
    border-radius: 0px 0px 5px 5px;
    background: none repeat scroll 0% 0% #121212 !important;
}
/* Quote Label*/
p.citation {
    font-size: 12px;
    padding: 8px 10px;
    border-left: 2px solid #4c4c3d !important;
    background: #292929 !important;
    border-top: 2px solid #4c4c3d !important;
    border-right: 2px solid #4c4c3d !important;
    border-radius: 5px 5px 0px 0px;
    font-weight: bold;
}

/*badges like "PINNED"*/
.ipsBadge {
    display: inline-block;
    height: 16px;
    line-height: 16px;
    padding: 0px 5px;
    font-size: 9px;
    font-weight: bold;
    text-transform: uppercase;
    color: #000000 !important;
    border-radius: 4px;
}

                                        /*============== Page selectors/indicators ==============*/
                                        
/*little Page number links when choosing thread*/
ul.mini_pagination li a {
    background: #000000 !important;
    border: 1px solid rgb(211, 211, 211);
    padding: 1px 3px;
}

/*The page you are currently on*/
.pagination .pages li.active {
    background: none repeat scroll 0% 0% #dfd122 !important;
    color: #000000 !important;
    font-weight: bold;
    border-radius: 2px;
    padding: 1px 5px;
}

/*"Next" and ">>" Buttons*/
.pagination .back a, .pagination .forward a {
    display: inline-block;
    padding: 0px 6px;
    height: 20px;
    background: none repeat scroll 0% 0% #2c2c2c !important;
    border-radius: 2px;
    text-transform: uppercase;
    color: rgb(90, 90, 90);
    font-size: 11px;
    font-weight: bold;
}

/*Page selection buttons on mouseover*/
.pagination .pages a:hover {
    background: none repeat scroll 0% 0% #2c2c2c !important;
    border-radius: 2px;
}




/*=======================================
/* Profile dropdown thing in upper rigth hand corner*/
.ipsHeaderMenu {
    background: -moz-linear-gradient(center top , #0a0a0a 0%, #0e0e0e 70%, #101010 100%) repeat scroll 0% 0% transparent !important;
    padding: 10px;
    border-radius: 0px 0px 6px 6px;
    overflow: hidden;
    width: 340px;
}


/*Side box at profile-> My content*/
.ipsBox_container {
    background: none repeat scroll 0% 0% #121212 !important;
    border: 1px solid rgb(219, 228, 239);
}

/*Sidebar on profile home*/
.ipsVerticalTabbed_tabs > ul {
    border-top: 1px solid #837b15 !important;
    border-left: 1px solid #837b15 !important;
}
.ipsVerticalTabbed_tabs li {
    background: none repeat scroll 0% 0% #151515 !important;
    color: rgb(128, 128, 128);
    border-bottom: 1px solid #837b15 !important;
    font-size: 13px;
}
.ipsVerticalTabbed_tabs li.active a {
    width: 135px;
    position: relative;
    z-index: 8000;
    border-right: 1px solid rgb(255, 255, 255);
    background: none repeat scroll 0% 0% #2c2c2c !important;
    color: rgb(53, 53, 53);
    font-weight: bold;
}
.ipsVerticalTabbed_tabs li a:hover {
    background: none repeat scroll 0% 0% #434343 !important;
    color: rgb(128, 128, 128);
}


                                        /*General Text Boxes*/
/*header*/
.general_box h3 {
    font: 14px helvetica,arial,sans-serif;
    padding: 8px 10px;
    background: none repeat scroll 0% 0% #434343 !important;;
    color: #ffffcc !important;
}
/*body*/
.general_box {
    background: none repeat scroll 0% 0% #151515 !important;
    margin-bottom: 10px;
    color: #ffffcc !important;
}
                                        /*============ Leftmost bar with "News", "Media", "Support Us" ============*/
/*background*/
#navigation {
    position: fixed;
    width: 99px !important; /* 0px to hide*/
    z-index: 100;
    font-size: 11px;
    font-family: verdana;
    background-color: #000000 !important;
    background-image: none !important;
    background-repeat: repeat-y;
    min-height: 100% !important;
}

/*buttons*/
#navigation ul li {
    height: 30px;
    line-height: 30px;
    list-style: none outside none;
    padding-left: 5px;
    color: #ffffcc !important;
    cursor: pointer;
    background-color: #151515 !important;
    width: 8em;
    border-style: outset;
    border-width: 1px;
}
/*buttons when mouseovered*/
#navigation ul li:hover {
    background-color: #2c2c2c !important;
    position: relative;
    color: #ffffcc !important;
}


                                        /*======================= Navigation Bar =======================*/
/*basic settins*/
#primary_nav
a{
	color:#fff;
	background:#000000 !important;
	display:block;
	padding:6px
15px 8px;
	text-shadow:0px 1px 1px rgba(0,0,0,0.5)
}

/*When moused over*/
#primary_nav a:hover, #primary_nav
a.menu_active{
	background:#383838 !important;
	color:#fff !important;
}



                                        /* =======================Table=======================*/
table{
	width:100% !important;
	
	background-color:#000000 !important; /*bottoms of the tables (adds)*/
}

}
