css:
@-moz-document url-prefix("http://dota2.ru"), url-prefix("http://dota2.ru") {
.main {
background: #1A1919;
position: relative;
padding: 15px;
}

.block-bg-color {
background: #2C2C2C;
}

.brdr-btm {
border-bottom: 1px solid #1A1A1B;
}

a {
color: #B9B9B8;
text-decoration: none;
}

.brdr-btm:hover {
background: #22232C;
cursor: pointer;
}

.tabContainer a:hover, .news-single-tab#tab0, .guides-tab:hover, .guides-tab.active {
background: #4C4D53 !important;
}

.tabContainer a {
display: block;
text-transform: uppercase;
line-height: 24px;
width: 99px;
text-align: center;
float: left;
background: #1D1C1C;
}

.page-content {
width: 710px;
margin: 0 15px;
float: left;
position: relative;
background: #2C2C2C;
}

.main {
background: #1A1919;
position: relative;
padding: 15px;
}

body {
font-size: 12px;
font-family: verdana,arial,sans-serif;
line-height: 1.5;
color: #CECFCF;
}

.news-comment-text {
padding: 5px 10px;
background: #2E2C2C;
}

.news-comment-info {
color: #777;
font-size: 11px;
padding: 10px;
height: 40px;
background: #1D1B1B;
}

.pagination a:hover, .trade-pagination a:hover, .current-page {
background: #424040;
}

.important-information {
background: #311F1F !important;
}

.news-more-information .link, .rating-buttons a {
float: left;
margin-right: 5px;
padding: 5px 10px;
background: #181717;
}

.pch-info, .trade-itmes-section {
font-size: 11px;
line-height: 30px;
width: 100%;
background: #1B1A1A;
padding: 0 15px;
box-sizing: border-box;
}

.page-single {
background: #363535;
margin: 0 0 15px;
}

.sm-tourname {
padding: 0 5px;
height: 21px;
width: 384px;
overflow: hidden;
display: none;
position: absolute;
text-align: center;
background: #44444B;
}

#heroes-choose {
padding: 20px;
height: 70px;
font-size: 14px;
text-align: center;
background: #1F1E1E;
}

.guides-wrapper .filter {
text-align: right;
font-size: 12px;
background: #202020;
margin-top: 10px;
padding: 10px;
}

.guides-wrapper .list {
padding: 15px;
background: #3D3C3C;
}

.guides-best-list .single {
background: #252424;
padding: 10px;
margin: 5px 0;
}

.guides-best-list .single:hover {
background: #181717;
}

.guides-view .main-information {
padding: 20px;
background: #252424;
}


.spoiler .spoiler-title {
padding: 10px 10px 10px 35px;
background: #1F1E1E;
}

.block-wrapper {
padding: 10px;
background: #272626;
margin: 0 0 15px;
}

.guides-view .contents a {
display: block;
text-decoration: none;
padding: 5px 10px;
background: #464444;
margin: 2px 0;
}

.guides-view .contents a:hover {
background: #222121;
}

.guides-view .spells .single {
position: relative;
padding: 10px;
background: #363636;
margin: 2px 0;
}

.guides-view span[style] {
color: #928E8E !important;
}

.news-more-information .link:hover, .rating-buttons a.active, .rating-buttons a:hover {
background: #4E4E4E;
}

.store-list .single:hover {
background: #1A1919;
}

.page-streams-single-one {
background: #222;
padding: 7px;
position: relative;
display: block;
float: left;
width: 333px;
margin: 0 16px 15px 0;
}

.psso-author, .psso-views {
margin: 2px 0 0 !important;
background: #313131;
padding: 0 5px;
font-size: 11px;
line-height: 14px;
}

.blogs .filters-bl {
background: #1D1D1D;
}

.post-wrapper {
background: #333232;
}

.blogs .post .categories {
text-transform: uppercase;
padding: 15px 15px 5px;
font-size: 11px;
background: #3D3D3D;
}

.blogs .post .other {
padding: 10px 15px;
background: #272525;
}

.blogs .post-view .post-text {
padding: 15px;
word-wrap: break-word;
background: #2E2D2D;
}

.blogs .post-view .desc {
text-transform: uppercase;
padding: 15px;
font-size: 11px;
background: #3D3C3C;
margin-bottom: 10px;
}

.blogs .post-view .title {
position: relative;
padding: 15px;
margin: 15px 0;
background: #1B1B1B;
}

.blogs .post-view .tags {
padding: 15px;
font-size: 11px;
margin-top: 10px;
background: #201F1F;
}

.blogs .last-comments .single, .blogs .feed .single {
display: block;
padding: 15px;
background: #222;
}

.blogs .last-comments .single:hover, .blogs .feed .single:hover {
background: #161616;
}

.blogs .blogs-list .single {
position: relative;
padding: 15px;
overflow: hidden;
background: #1D1D1D;
margin-bottom: 1px;
}

.trade .single .get, .trade .single .send {
width: 400px;
background: #1D1C1C;
padding: 10px;
vertical-align: top;
}

.prices-line-mini .prices-item-mini-title {
width: 87px;
height: 39px;
padding: 5px;
overflow: hidden;
font-size: 11px;
line-height: 14px;
background: #202020;
}

.prices-item-effect {
float: left;
width: 335px;
font-size: 11px;
margin: 1px;
padding: 5px;
background: #252942;
}

.guides-tactics {
padding: 10px;
background: #1A1919;
}

#video .list:hover {
background: #333232;
}

.sweetbox-wrapper {
position: absolute;
top: 20px;
left: 50%;
margin-left: -300px;
width: 600px;
padding: 5px 10px;
z-index: 9100;
background: #181717;
}

.sweetbox-wrapper .views {
float: left;
padding: 5px 10px;
line-height: 18px;
background: #292727;
text-decoration: none;
margin: 0 5px 0 0!important;
}

.sweetbox-wrapper .tags .single {
background: #252424;
}

#gallery-tags {
padding: 10px;
margin-bottom: 5px;
background: #292828;
}

#gallery-tags .title {
text-align: center;
padding: 5px;
margin: 3px 0;
background: #363B42;
}

#gallery-tags .title:hover {
background: #273141;
cursor: pointer;
}
}
