css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body, input, button, textarea 
{font-family:Open Sans Light!important;}
    
A.g-hovercard.yt-uix-sessionlink.yt-user-name,.multirow-shelf .yt-uix-expander-head,.action-panel-content a 
{color: rgb(100,100,100)!important;}

A.yt-uix-sessionlink
,.video-player-view-component a,A.lohp-video-link.max-line-2.yt-uix-sessionlink,.yt-ui-ellipsis-wrapper a,.yt-uix-button-link:active, .yt-uix-button-link,
#masthead-expanded .masthead-expanded-menu-item a
,.yt-lockup-title a,  .yt-lockup-meta a, .feed-item-header a
{color: rgb(51, 51, 51)!important;}

A.yt-uix-sessionlink:hover
,A.spell-correction-given-query,.video-player-view-component a:hover,.xta .obc:hover
,.branded-page-module-title a.branded-page-module-title-link:hover,A.lohp-video-link.max-line-2.yt-uix-sessionlink:hover,
.not-exp-new-metadata-style .lohp-video-link:hover,a.gssb_j,.yt-ui-ellipsis-wrapper a:hover,
A.spell-correction-suggested-query,A.spell-correction-suggested-query,A.yt-uix-redirect-link:hover,.yt-uix-button-link:active:hover, .yt-uix-button-link:hover,
#masthead-expanded .masthead-expanded-menu-item a:hover,#watch7-sidebar .video-list-item a:hover .title 
,#watch7-user-header .yt-user-name:hover,.multirow-shelf .yt-uix-expander-head:hover,
#subscription-manager-container .subscription-title:hover, 
#subscription-manager-container .collection-title:hover ,
.feed-header .feed-manage-link:hover ,
.yt-lockup:hover a,.yt-lockup:hover .yt-lockup:hover 
.yt-lockup-description a,.feed-item-container:hover, 
.feed-item-container:hover a.yt-user-name:hover
{color: rgb(180, 24, 30)!important;
text-decoration: none!important;}
    A.guide-item.yt-uix-sessionlink.yt-valign.spf-link:hover,A.creator-sidebar-section-link.yt-uix-sessionlink:hover{color:rgb(243,243,243)!important;}

.branded-page-related-channels h3 a, .branded-page-related-channels h3{color:rgb(0,0,0)!important}


item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text, .guide-collection-item .guide-item.guide-item-selected {
    background: none repeat scroll 0% 0% rgb(180, 24, 30)!important;
        background-color: rgb(180, 100, 100)!important;}
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text, .guide-collection-item .guide-item.guide-item-selected {
    background: none repeat scroll 0% 0% rgb(180, 24, 30)!important;}
.video-extras-sparkbar-likes 
{background-color: rgb(10, 180, 100)!important;}
.video-extras-sparkbar-dislikes 
{background-color: rgb(255, 0, 0)!important;}

A.guide-item.yt-uix-sessionlink.yt-valign.spf-nolink:hover,
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text, .guide-collection-item .guide-item.guide-item-selected {
    color: rgb(255, 255, 255)!important;}
#yt-masthead-container {background-color: white!important;}
IMG#logo,A#logo-container,IMG.yt-uix-button-arrow {display: none!important; opacity: 0!important;}
}
