css:
@-moz-document url-prefix("http://freelansim.ru/") {
.task__title {
  	margin-bottom: 5px;
}

.task__title a {
	color: #666666 !important;
  	font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}

.task__title a:visited {
	color: #B5B5B5 !important;
  	font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}

.params__price {
	color: #4abfb4;
    min-width: 160px;
    display: inline-block;
  	font: bold 13px Verdana, Tahoma, sans-serif !important;
}

.params__price .negotiated_price {
	color: #878B8D;
}
    
.task__column_price .negotiated_price {
	color: #878B8D;
	font: bold 13px Verdana, Tahoma, sans-serif !important;
}
  
.params__published-at {
	color: #666666 !important;
}

.tags__item_link, .tags__item_tag {
	color: #666666 !important;
}

.tags__item_link, .tags__item_tag {
	border: 2px solid #B3B3B3;
}
  
.layout-block__title {
	font: bold 14px Verdana, Tahoma, sans-serif !important;
}
  
.layout-block_categories .layout-block__title {
	color: #4f6173 !important;
}

.about2 strong {
	font: bold 11px Verdana, Tahoma, sans-serif !important;
}

.job_mark .job__title a {
	font: bold 12px Verdana, Tahoma, sans-serif !important;
}

.job__company a {
	color: #AFAFAF !important;
}

.job_mark .job__company a {
	color: #4abfb4 !important;
}
      
.menu_footer .link {
  	color: #9aa9b3;
	font: bold 13px Verdana, Tahoma, sans-serif !important;
}
          
          
.user-data__title a {
	color: #666666 !important;
  	font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}
            
.user-data__spec {
	color: #AFAFAF;
	margin-top: 2px;
}
              
.user__price span {
	font: bold 13px Verdana, Tahoma, sans-serif !important;
	margin-top: 1px;
}
                
.user__price .suffix {
	color: #AFAFAF;
	font: normal 12px Verdana, Tahoma, sans-serif !important;
	margin-top: 2px;
}
                    
.checkbox_flat input[type="checkbox"]:checked+.checkbox__label {
	font-weight: normal;
}
                        
.task_detail .task__title {
	color: #666666 !important;
  	font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}
                            
.task__description strong {
	font: bold 13px Verdana, Tahoma, sans-serif !important;
}
                              
.user-params__property {
	font: bold 14px Verdana, Tahoma, sans-serif !important;
}
                                
.data_statistics .data__value {
	font: bold 14px Verdana, Tahoma, sans-serif !important;
	margin-top: 2px;
}
                                
.task__price .count {
	font: bold 14px Verdana, Tahoma, sans-serif !important;
}
                                    
.task__price .suffix {
	color: #AFAFAF;
	font: normal 12px Verdana, Tahoma, sans-serif !important;
	margin-top: 2px;
}
                                        
.form-section legend {
	font: bold 14px Verdana, Tahoma, sans-serif;
}
  
.table_payment .payment-price {
	font: bold 16px Verdana, Tahoma, sans-serif;
}

.table_payment .payment-duration {
	font: bold 13px Verdana, Tahoma, sans-serif;
}

.form__label {
	color: #AFAFAF;
	font: bold 12px Verdana, Tahoma, sans-serif !important;
}

.price_payment {
	font: bold 16px Verdana, Tahoma, sans-serif;
}

.date_active {
	font: bold 14px Verdana, Tahoma, sans-serif;
}

.menu_tabbed .menu__link {
	color: #697A91;
}

.menu_tabbed .link_active {
	color: #ffffff !important;
}

.table_payment .form-control_money {
	font: bold 16px Verdana, Tahoma, sans-serif;
}

.empty-block__title {
	font: normal 20px Verdana, Tahoma, sans-serif;
}

.empty-block {
	color: #aeb8bc;
}

.profile-blocks__title {
	font: bold 16px Verdana, Tahoma, sans-serif;
}

.informer__title {
	font: bold 12px Verdana, Tahoma, sans-serif;
 	margin-bottom: 8px;
}

.pane__message-title {
	font: bold 12px Verdana, Tahoma, sans-serif;
	margin-bottom: 14px;
}

.informer__pane .price {
	font: bold 16px Verdana, Tahoma, sans-serif;
}

.menu_user-settings .menu__link {
	font: bold 13px Verdana, Tahoma, sans-serif;
	color: #aeb8bc;
}

.menu_user-settings .menu__link_active {
	font: bold 13px Verdana, Tahoma, sans-serif;
	color: #5d6577;
}

.form-fields__group label {
	font: bold 12px Verdana, Tahoma, sans-serif;
	margin-bottom: 10px;
}

.checkbox__label {
	color: #878B8D;
    padding-top: 3px;
}
  
.user-plank__username {
	font: bold 13px Verdana, Tahoma, sans-serif;
}

.socials-accounts__title {
	font: bold 15px Verdana, Tahoma, sans-serif;
}

.data_activity .data__value {
	font: bold 14px Verdana, Tahoma, sans-serif;
}

.profile-blocks__settings {
	top: 1px;
}

.params__count {
	font-weight: normal;
}

.params__views, .params__responses, .params__published-at {
	color: #666666 !important;
}

.icon_profile_views:before, .icon_task_views:before {
	margin-right: 0px;
}

.icon_profile_answers:before, .icon_task_responses:before {
	margin-right: 0px;
}
}
