css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {
    :root {
        background: rgb(48,48,48) url("chrome://browser/skin/steva_bg.png") repeat-x fixed;
    }
    
    .launchButton {
        color: #a6a6a6;
    }
    
    .snippet p {
        color: #a6a6a6;
    }
    
    .snippet a {
        color: rgb(0,173,238);
    }
    
    .snippet a:hover {
        color: rgb(0,173,238);
    }
    
    #searchText {
        background: rgba(0,0,0,.35) !important;
        color: #a6a6a6;
    }
    
    #searchSubmit {
        background: linear-gradient(rgba(255,255,255,0.3), rgba(255,255,255,0.05)) repeat scroll 0% 0% padding-box transparent;
        color: #a6a6a6;
    }
}
