css:
@-moz-document regexp("(http|https)://thepiratebay.*") {
/* 
 * Name:    The Pirate Bay - Sleek Dark
 * Title:   A darker theme which is much easier on the eye when browsing the website
 * Author:  Crystallis
 * Version: 0.7
 *
 */

body {
color: #9a9a9a ! important;
background: #141414 ! important;
}

/* option the remove promos from the front page */
/*[[nopromo]]*/


#fp h1 a {
background: url('http://i.imgur.com/oKDQBZV.png') no-repeat ! important;
outline: none !important;
}
img[src*="/img/tpblogo_sm_ny.gif"] {
margin-top:0 ! important;
width: 0 ! important;
height: 87px ! important;
padding-left:87px ! important;
background:url(http://i.imgur.com/OG59ZHU.png) no-repeat ! important;
}
a, a:link, a:visited, a:focus {
color: #00ADEE ! important;
border-bottom: 1px dotted transparent ! important;
}
a:hover{
text-shadow: 0px 0px 5px rgba(0,173,238,.9) ! important;
transition: .1s ease-in ! important;
}
h2 {
margin: 0 auto ! important;
border-top: 1px solid #262626 ! important;
border-bottom: 1px solid #141414 ! important;
background: #1e1e1e ! important;
color: #a6a6a6 ! important;
}
select {
background: rgba(0,0,0,.9) ! important;
color: #a6a6a6 ! important;
overflow: hidden ! important;
-webkit-appearance: none ! important;
-moz-appearance: none ! important;
}
option {
background: rgba(0,0,0,.9) ! important;
box-shadow: 0px 2px 4px rgba(0,0,0,.5) ! important;
border: 1px solid rgb(0,0,0) ! important;
color: #a6a6a6 ! important;
overflow: hidden ! important;
-moz-appearance: none ! important;
}
thead#tableHead th {
background: #1e1e1e ! important;
}
table#searchResult th, table#searchResult td {
border: 1px solid #333333 ! important;
}
table#searchResult tr {
background: #202020 ! important;
}
table#searchResult tr.alt {
background: #F2ECE7;
}
table#searchResult tr:hover {
background: #141414 ! important;
}
table#searchResult td.vertTh {
color: #4e5456 ! important;
}
#detailsframe {
border-top: 1px solid #232323 ! important;
background: #2d2d2d ! important;
margin: 0 auto ! important;
}
#detailsframe #title {
border-bottom: 1px solid #141414 ! important;
}
#detailsframe #title {
background: #1e1e1e ! important;
color: #a6a6a6 ! important;
border-top: 1px solid #262626 ! important;
}
#details {
background: #202020 ! important;
}
#details h4 {
color: #888888 ! important;
}
#details dt {
color: #a6a6a6 ! important;
border-bottom: 1px dashed transparent ! important;
}
#details .col1 {
float: right ! important;
}
#details #CommentDiv {
background-color: #202020 ! important;
}

#details dd {
border-bottom: 1px dashed transparent ! important;
}
.nfo {
border: 1px solid #333333 ! important;
background: #141414 ! important;
}
p.info textarea {
width: 585px ! important;
background: #333333 ! important;
color: #dddddd ! important;
border: none ! important;
padding: 4px ! important;
}
.download a {
color: #00ADEE ! important;
}
.download a:hover {
color: #00ADEE ! important;
border-bottom: 1px solid transparent ! important;
}
.detailartist a {
color: #008E00 ! important;
}
#comments p.byline {
color: #666666 ! important;
}
#comments .comment {
border: 1px solid #333333 ! important;
background: #141414 ! important;
padding: 4px ! important;
}
* html .rating {
margin-left: 110px ! important;
}
textarea[name="aboutyou"] {
border-color: transparent ! important;
color: #dddddd ! important;
background: #333333 ! important;
}
hr {
color: #1e1e1e ! important;
}
.doodle {
border-top: 1px solid #1e1e1e ! important;
}
.whip img {
width: auto ! important;
}
.policy {
width: 42.4em ! important;
}
.cover h3, .morealbums h3, .upcomingevents h3 {
border: 0 ! important;
}
input[type="search"] {
padding: 4px ! important;
background: #333333 ! important;
color: #dddddd ! important;
border: none ! important;
}
input[type="password"] {
padding: 4px ! important;
background: #333333 ! important;
color: #dddddd ! important;
border: none ! important;
}
input[type="text"] {
background: #333333 ! important;
color: #dddddd ! important;
border: none ! important;
padding: 4px ! important;
}
#err {
border: 1px solid #232323 ! important;
background: #2d2d2d ! important;
}
#detailsouterframe {
padding: 10px ! important;
}
.uldetails label {
color: #a6a6a6 ! important;
}
.uldetails td {
color: #a6a6a6 ! important;
border-bottom: 1px dashed #202020 ! important;
}
.uldetails text, #tname, #torrent {
width: 200px ! important;
}
.uldetails .nfo {
color: #9a9a9a ! important;
}
::-webkit-scrollbar {
width: 10px ! important;
height: 10px ! important;
background: rgb(30, 30, 30) ! important;
border: 1px solid rgb(37, 37, 37) ! important;
}
::-webkit-scrollbar-button {
display: none ! important;
}
::-webkit-scrollbar-thumb,
::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}
::-webkit-scrollbar-thumb {
min-height: 28px ! important;
background: rgb(78, 78, 78) ! important;
}
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track {
background: rgb(30, 30, 30) ! important;
}
}

@-moz-document domain("promobay.org") {
body {
color: #9a9a9a ! important;
background-color: #141414 ! important;
}
a {
color: #00ADEE ! important;
border-bottom: 1px dotted transparent ! important;
}
a:hover{
text-shadow: 0px 0px 5px rgba(0, 173, 238, .9) ! important;
transition: .1s ease-in ! important;
}
.alert-success {
background-color: #1e1e1e ! important;
border-color: #242424 ! important;
}
.alert {
color:  #9a9a9a ! important;
}
aside.sidebarz {
background: #202020 ! important;
overflow: hidden ! important;
border-right: 1px solid #1e1e1e ! important;
}
a.brand {
color: #808080 ! important;
}
a.brand:hover {
background: #2e2e2e ! important;
color: #a0a0a0 ! important;
}
.autoplay p {
color: #808080 ! important;
}
.nav-list-vivid > li.active > a, .nav-list-vivid > li.active > a:hover, .nav-list-vivid > li.active > a:focus {
background-color: #333333 ! important;
color: #a0a0a0 ! important;
}
.nav-list-vivid > li > a {
color: #707070 ! important;
}
.nav-list-vivid > li > a:hover, .nav-list-vivid > li > a:focus {
color: #a0a0a0 ! important;
text-shadow: none ! important;
text-decoration: none ! important;
-webkit-transition: 0.25s ! important;
-moz-transition: 0.25s ! important;
-o-transition: 0.25s ! important;
transition: 0.25s ! important;
}
.nav-counter {
background-color: #333333 ! important;
color: #999999 ! important;
}
aside .withlove {
color:  #9a9a9a ! important;
}
.has-switch span.switch-right {
background-color: #2e2e2e ! important;
color: #808080 ! important;
}
.has-switch > div.switch-off label {
background-color: #262626 ! important;
border-color: #262626 ! important;
-webkit-box-shadow: -1px 0 0 rgba(0, 0, 0, 0.5) ! important;
-moz-box-shadow: -1px 0 0 rgba(0, 0, 0, 0.5) ! important;
box-shadow: -1px 0 0 rgba(0, 0, 0, 0.5) ! important;
}
.has-switch span.switch-left {
background-color: #2e2e2e ! important;
color: #a0a0a0 ! important;
}
.has-switch > div.switch-on label {
background-color: #262626 ! important;
}
.switch-square label {
border-color: #262626 ! important;
}
.btn-social-facebook {
background-color: #262626 ! important;
}
.btn-social-facebook:hover, .btn-social-facebook:focus {
background-color: #222222 ! important;
}
.btn.btn-info {
background-color: #262626 ! important;
}
.btn.btn-info:hover, .btn.btn-info:focus, .btn-group:focus .btn.btn-info.dropdown-toggle {
background-color: #222222 ! important;
}
.btn:hover, .btn:focus, .btn-group:focus .btn.dropdown-toggle {
color: #a0a0a0 ! important;
text-shadow: 0px 0px 5px rgba(160, 160, 160, .9) ! important;
}
.nav-pills > li > a {
background-color: #262626 ! important;
border-left: 2px solid #202020 ! important;
color: #a0a0a0 ! important;
}
.nav-pills > li > a:hover, .nav-pills > li > a:focus {
background-color: #222222 ! important;
color: #a0a0a0 ! important;
text-shadow: 0px 0px 5px rgba(160, 160, 160, .9) ! important;
}
hr {
border-top: 1px solid #1e1e1e ! important;
border-bottom: 1px solid #2e2e2e ! important;
}
.termz {
background: #181818 ! important;
color: #9a9a9a ! important;
}
::-webkit-scrollbar {
width: 10px ! important;
height: 10px ! important;
background: rgb(30, 30, 30) ! important;
border: 1px solid rgb(37, 37, 37) ! important;
}
::-webkit-scrollbar-button {
display: none ! important;
}
::-webkit-scrollbar-thumb,
::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}
::-webkit-scrollbar-thumb {
min-height: 28px ! important;
background: rgb(78, 78, 78) ! important;
}
::-webkit-scrollbar-corner,
::-webkit-scrollbar-track {
background: rgb(30, 30, 30) ! important;
}
}

