css:
@-moz-document domain("boards.4chan.org") {

/* General
========== */

html, body {
    background: #fff !important;
    color: #3d4b4f !important;
    font-family: "Univers LT 55", sans-serif !important;
    font-size: 10pt;
    margin: 0 !important;
    padding: 0 !important;
}

form,
input,
.field {
    font-family: "Univers LT 55", sans-serif !important;
}

/* Checkboxes */
input[type="checkbox"] {
    -moz-appearance: none !important;
    background: #fff !important;
    border: 1px solid #e3e7e8 !important;
    box-shadow: none !important;
    border-radius: 2px !important;
    color: #5fb3ce !important;
    cursor: pointer;
}

input[type="checkbox"]:checked {
    background: #5fb3ce !important;
    color: #fff !important;
    border-color: #4199b5 !important;
}

/* Cleanup
========== */
.boardBanner,
hr,
.navLinks,
.qr-link-container,
.deleteform.desktop,
.stylechanger.desktop,
#absbot,
.sideArrows,
.postInfo.desktop > input,
#scroll-marker,
.stub .show-thread-button .fourchanx-link,
.stub .show-reply-button .fourchanx-link,
.ad-plea,
.bottomCtrl {
    display: none !important;
}

/* Header
========= */
#header-bar {
    height: 40px !important;
    padding: 0 80px 0 80px !important;
    
    
    background: rgba(255,255,255,1.0) !important;
    box-shadow: none !important;
    border: none !important;
    /*border-top: 8px solid rgba(79,93,97,1.0) !important;*/
    opacity: 0.65 !important;
    
    font-size: 0 !important;
    line-height: 40px !important;
    color: transparent !important;
    transition: all 0.3s ease 0s !important;
}

#header-bar a,
#header-bar #updater,
#header-bar #thread-stats,
#header-bar .menu-button {
    color: #4f5d61 !important;
    font-size: 17px !important;
    transition: all 0.2s ease 0s !important;
}

#header-bar #shortcuts {
    height: 32px !important;
}

#header-bar #post-count.warning {
    color: #ce6a5f !important;
}

#header-bar .menu-button {
    height: 26px !important;
    margin-left: 0 !important;
    margin-right: 8px !important;
}

#header-bar a,
#header-bar #updater,
#header-bar #thread-stats {
    margin: 0 !important;
}

#header-bar .settings-link {
    margin-right: 8px !important;
}

#header-bar a:first-child {
    margin-left: 8px !important;
    /*text-transform: lowercase !important;*/
}

#header-bar a.expand-all-shortcut {
    opacity: 1 !important;
}

#header-bar a.current,
#header-bar #update-status.new {
    color: #4f5d61 !important;
}

#header-bar a,
#header-bar .menu-button,
#header-bar #updater {
    transition: all 0.2s ease 0s;
}

#header-bar a:hover,
#header-bar .menu-button:hover,
#header-bar #updater:hover {
    color: #2f6e82 !important;
    border-bottom: 1px solid rgba(95,179,206,0.6) !important;
    transition: all 0.2s ease 0s;
}

/* Hide navigation until header is hovered */
#header-bar #custom-board-list {
    position: absolute !important;
    top: 0 !important;
}

#header-bar #custom-board-list a {
    opacity: 0 !important;
    margin: 8px !important;
    transition: all 0.2s ease 0s;
}

#header-bar:hover #custom-board-list a {
    opacity: 1 !important;
    transition: all 0.2s ease 0s;
}

/* Header state when hovered */
#header-bar:hover {
    background: rgba(255,255,255,0.90) !important;
    border-color: rgba(95,179,206,1.0) !important;
    opacity: 1 !important;
    transition: all 0.3s ease 0s !important;
}

#header-bar:hover a,
#header-bar:hover #updater,
#header-bar:hover #thread-stats,
#header-bar:hover .menu-button {
    color: #5fb3ce !important;
    transition: all 0.2s ease 0s !important;
}

#header-bar:hover a:hover,
#header-bar:hover .menu-button:hover,
#header-bar:hover #updater:hover {
    color: #2f6e82 !important;
    border-bottom: 1px solid rgba(95,179,206,0.6) !important;
    transition: all 0.2s ease 0s;
}

/* Settings icon */
#header-bar .settings-link {
    display: inline-block !important;
    font-size: 0 !important;
    color: transparent !important;
    height: 28px !important;
    margin-top: 0px !important;
}

#header-bar .settings-link:after {
    content: url(data:image/png;base64,);
    font-size: 16px !important;
}

#header-bar:hover .settings-link:after {
    font-size: 16px !important;
    color: #5fb3ce !important;
    content: url(data:image/png;base64,);
    opacity: 1 !important;
    transition: all 0.2s ease 0s;
}

#header-bar:hover .settings-link:hover {
    border: none !important;
    transition: all 0.2s ease 0s;
}

#header-bar .settings-link:hover::after,
#header-bar .settings-link:active::after {
    content: url(data:image/png;base64,);
    transition: all 0.2s ease 0s;
}

/* QR icon */
#header-bar .qr-shortcut {
    display: inline-block !important;
    font-size: 0 !important;
    color: transparent !important;
    height: 28px !important;
    margin-top: 0px !important;
}

#header-bar .qr-shortcut:after {
    content: url(data:image/png;base64,);
    font-size: 16px !important;
}

#header-bar:hover .qr-shortcut:after {
    font-size: 16px !important;
    color: #5fb3ce !important;
    content: url(data:image/png;base64,);
    opacity: 1.0 !important;
    transition: all 0.2s ease 0s;
}

#header-bar:hover .qr-shortcut:hover {
    border: none !important;
    transition: all 0.2s ease 0s;
}

#header-bar .qr-shortcut:hover::after,
#header-bar .qr-shortcut:active::after {
    content: url(data:image/png;base64,);
    transition: all 0.2s ease 0s;
}

/* Expand images icon */
#header-bar .expand-all-shortcut {
    display: inline-block !important;
    font-size: 0 !important;
    color: transparent !important;
    height: 28px !important;
    margin-top: 0px !important;
}

#header-bar .expand-all-shortcut:after {
    content: url(data:image/png;base64,);
    font-size: 16px !important;
}

#header-bar:hover .expand-all-shortcut:after {
    font-size: 16px !important;
    color: #5fb3ce !important;
    content: url(data:image/png;base64,);
    opacity: 1 !important;
    transition: all 0.2s ease 0s;
}

#header-bar:hover .expand-all-shortcut:hover {
    border: none !important;
    transition: all 0.2s ease 0s;
}

#header-bar .expand-all-shortcut:hover::after,
#header-bar .expand-all-shortcut:active::after {
    content: url(data:image/png;base64,);
    transition: all 0.2s ease 0s;
}

#header-bar .contract-all-shortcut {
    display: inline-block !important;
    font-size: 0 !important;
    color: transparent !important;
    height: 28px !important;
    margin-top: 0px !important;
}

#header-bar .contract-all-shortcut:after {
    content: url(data:image/png;base64,);
    font-size: 16px !important;
}

#header-bar:hover .contract-all-shortcut:after {
    font-size: 16px !important;
    color: #5fb3ce !important;
    content: url(data:image/png;base64,);
    opacity: 1 !important;
    transition: all 0.2s ease 0s;
}

#header-bar:hover .contract-all-shortcut:hover {
    border: none !important;
    transition: all 0.2s ease 0s;
}

#header-bar .contract-all-shortcut:hover::after,
#header-bar .contract-all-shortcut:active::after {
    content: url(data:image/png;base64,);
    transition: all 0.2s ease 0s;
}

/* Menu button icon */
#header-bar .menu-button {
    display: inline-block !important;
    font-size: 0 !important;
    color: transparent !important;
    height: 28px !important;
    margin-top: 0px !important;
}

#header-bar .menu-button i,
#header-bar .menu-button i:hover {
    font-size: 0 !important;
    color: transparent !important;
    margin: 0 !important;
    display: none !important;
}

#header-bar .menu-button:after {
    content: url(data:image/png;base64,);
    font-size: 16px !important;
}

#header-bar:hover .menu-button:after {
    font-size: 16px !important;
    color: #5fb3ce !important;
    content: url(data:image/png;base64,);
    opacity: 1 !important;
    transition: all 0.2s ease 0s;
}

#header-bar:hover .menu-button:hover {
    border: none !important;
    transition: all 0.2s ease 0s;
}

#header-bar .menu-button:hover::after,
#header-bar .menu-button:active::after {
    content: url(data:image/png;base64,);
    transition: all 0.2s ease 0s;
}

/* Messages */
.notification {
    box-shadow: none !important;
    margin-top: -40px !important;
    height: 40px !important;
    overflow: hidden !important;
    background: transparent !important;
}

.notification .message {
    color: #4f5d61 !important;
    background: transparent !important;
    text-shadow: none !important;
    font-size: 11pt !important;
    height: 40px !important;
    overflow: hidden !important;
    padding: 0 6px 0 6px !important;
}

.notification.warning .message {
    color: #ce6a5f !important;
}

.notification .close {
    text-shadow: none !important;
    padding: 0 6px 0 6px !important;
}

/* Main page
============ */
.board {
    margin: 38px 0 20px 0 !important;
    padding: 0 !important;
}

.thread {
    margin: 0 0 0 0 !important;
    padding: 0 !important;
    border-top: 1px solid #d9ddde !important;
}

#delform {
    margin: 0 !important;
    padding: 0 !important;
}

/* Why is this margin there in opened threads only? */
html.fixed {
    margin-top: 0 !important;
    border: none !important;
}

/* Front page */
.summary {
    width: auto !important;
    margin-left: 88px !important;
    color: #99a6aa !important;
    font-size: 12px !important;
    line-height: 21px !important;
    
    border-bottom: 1px solid transparent;
    transition: all 0.2s ease 0s;
    z-index: 10 !important;
}

.abbr {
    color: #7c878a !important;
    font-size: 12px !important;
    line-height: 21px !important;
    z-index: 10 !important;
}

.post.op .postNum span {
    color: transparent !important;
    font-size: 0 !important;
}

/* Pages */
.pagelist {
    background: transparent !important;
    border: none !important;
    border-top: 1px solid #edf1f2 !important;
    
    margin: 0 auto !important;
    float: none !important;
    padding: 20px 88px 20px 88px !important;
    
}

.pages {
    font-size: 0 !important;
    color: transparent !important;
    height: 12px !important;
}

.pages a {
    line-height: 12px !important;
    font-size: 13px !important;
    color: #99a6aa !important;
    padding: 4px 5px 4px 5px !important;
    
    transition: all 0.2s ease 0s;
}

.pages a:hover {
    color: #326085 !important;
    transition: all 0.2s ease 0s;
}

.pages strong a,
.pages strong a:hover {
    color: #4f5d61 !important;
}

.pagelist .prev,
.pagelist .next {
    text-transform: uppercase !important;
    color: #b1bfc4 !important;
    font-size: 12px !important;
    line-height: 12px !important;
}

.pagelist .prev input,
.pagelist .next input {
    -moz-appearance: none !important;
}

.pages.cataloglink {
    border-left: none !important;
    padding: 0 !important;
    margin-top: 6px !important;
}

/* Buttons */
.post.op .postNum .replylink,
.pagelist .prev input,
.pagelist .next input,
.pages.cataloglink a {
    font-size: 12px !important;
    font-weight: normal !important;
    text-decoration: none !important;
    text-transform: uppercase !important;
    transition: all 0.3s ease 0s;

    color: #fff !important;
    background: #5f9cce !important;
    border: none !important;
    border-radius: 2px !important;
    padding: 4px 8px 4px 8px !important;
    cursor: pointer;
}

.post.op .postNum .replylink:hover,
.pagelist .prev input:hover,
.pagelist .next input:hover,
.pages.cataloglink a:hover {
    background: #326085 !important;
    transition: all 0.3s ease 0s;
}

.pages.cataloglink a {
    padding: 5px 8px 5px 8px !important;
}

/* Global message
================= */
#globalMessage,
.closed {
    margin-top: 50px !important;
    color: #293336 !important;
    font-size: 21px !important;
    margin-left: 88px !important;
    margin-right: 88px !important;
}

.hide-announcement {
    font-size: 0 !important;
    color: transparent !important;
    margin-left: 48px !important;
    margin-top: 6px !important;
}

.hide-announcement:after {
    content: "Ã—";
    font-size: 16px !important;
    transition: all 0.3s ease 0s;
    
    color: #fff !important;
    background: #5f9cce !important;
    border: none !important;
    border-radius: 2px !important;
    padding: 0 10px 2px 10px !important;
}

.hide-announcement:hover:after {
    background: #326085 !important;
    transition: all 0.3s ease 0s;
}

/* Updater and thread stats
=========================== */
#thread-stats .move,
#updater {
    position: fixed !important;
    padding: 4px 8px 4px 8px !important;
    background: rgba(255,255,255,0.25) !important;
    cursor: default !important;
}

#updater,
#thread-stats {
    font-size: 11px !important;
    color: #b1bfc4 !important;
}

#thread-stats .move {
    bottom: 4px !important;
    right: 0px !important;
}

#updater {
    top: auto !important;
    bottom: 20px !important;
    right: 0px !important;
}

#update-status.new {
    color: rgba(95,179,206,1.0) !important;
}

/* Posts
======== */

/* General */
.post.op {
    display: table !important;
}

.post.reply,
.post.op {
    position: relative !important;
    border: none !important;
    width: 100% !important;
    margin: 0 !important;
    padding: 10px 4px 10px 4px !important;
    transition: all 0.2s ease 0s;
    
    background: transparent !important;
}

.postContainer.replyContainer {
    padding-right: 84px !important;
    padding-left: 84px !important;
    margin: 0 !important;
    border-top: 1px solid #edf1f2 !important;
}

.postContainer.opContainer {
    padding-right: 84px !important;
    padding-left: 84px !important;
}

.postMessage {
    font-size: 13px !important;
    margin: 5px 0 5px 15px !important;
    line-height: 21px !important;
}

/* Spoilers */
.postMessage s {
    border-radius: 1px !important;
    padding: 0 2px 0 2px !important;
    background: #3d4b4f !important;
    color: #3d4b4f !important;
    transition: all 0.2s ease 0s;
}

.postMessage s:hover {
    color: #fff !important;
    transition: all 0.2s ease 0s;
}

/* Highlights */
.postContainer.replyContainer.yourPost {
    border-left: 8px solid rgba(95,179,206,0.6) !important;
    padding-left: 76px !important;
}

.postContainer.replyContainer.quotesYou {
    border-left: 8px solid rgba(95,179,206,0.3) !important;
    padding-left: 76px !important;
}

.postContainer.replyContainer .qphl,
.postContainer.opContainer .qphl {
    outline: none !important;
    transition: all 0.2s ease 0s;
    border-left: 8px solid #5fb3ce !important;
    margin-left: 4px !important;
}

/* Post info & file info */
.postInfo.desktop {
    display: block !important;
    width: 100% !important;
    line-height: 21px !important;
    height: 21px !important;
}

.postNum {
    text-transform: lowercase !important;
}

.postInfo .menu-button,
.stub .menu-button {
    display: block !important;
    float: right !important;
    color: transparent !important;
    font-size: 0 !important;
    margin-right: 2px !important;
}

.dateTime,
.postNum,
.posteruid {
    font-size: 12px !important;
}

.fileInfo {
    margin-left: 0 !important;
    line-height: 21px !important;
    font-size: 12px !important;
    
    display: block !important;
    position: absolute !important;
    width: auto !important;
    height: 21px !important;
    bottom: 0 !important;
    right: 2px !important;
    text-align: right !important;
}

.fileThumb {
    margin-left: 0 !important;
    margin-top: 6px !important;
}

.fileThumb img {
    border-radius: 2px !important;
}

/* Hide menu button and file info until a post is hovered */
.postInfo .menu-button i,
.stub .menu-button i {
    color: transparent !important;
    font-size: 0 !important;
    opacity: 0 !important;
    transition: all 0.2s ease 0s;
    height: 16px !important;
}

.fileInfo {
    opacity: 0 !important;
    transition: all 0.2s ease 0s;
}

.postInfo .menu-button i:after,
.stub .menu-button i:after {
    font-size: 16px !important;
    content: url(data:image/png;base64,);
}

.postInfo .menu-button i:hover:after,
.stub .menu-button i:hover:after {
    content: url(data:image/png;base64,);
}

.postContainer:hover .menu-button i,
.stub:hover .menu-button i,
.postContainer:hover .fileInfo {
    opacity: 1 !important;
    transition: all 0.2s ease 0s;
}

.fileText a,
.menu-button.fourchanx-link.active i {
    opacity: 1 !important;
}

.menu-button.fourchanx-link.active i {
    color: transparent !important;
    font-size: 0 !important;
}

.menu-button.fourchanx-link.active i:after {
    font-size: 16px !important;
    content: url(data:image/png;base64,);
}

/* Menu */
#menu,
#menu .dialog {
    background: #fff !important;
    border: 1px solid #e3e7e8 !important;
    margin: 2px 1px 0 1px !important;
    color: #3d4b4f !important;
    box-shadow: none !important;
}

#menu .submenu {
    margin-top: -1px !important;
}

#menu .entry {
    border: 1px solid transparent !important;
    transition: all 0.1s ease 0s;
}

#menu .entry:hover {
    background: #5fb3ce !important;
    color: #fff !important;
    transition: all 0.1s ease 0s;
}

#menu .entry:hover input:active {
    background: #fff !important;
    border: 1px solid #e3e7e8 !important;
    box-shadow: none !important;
}

/* Append the content of the email field to the poster's name when hovered */
.nameBlock a:hover::after {
     content: "(" attr(href) ")";
     margin-left: 3px;
     color: #7c878a !important;
     transition: all 0.2s ease 0s;
}

/* Replace backlinks by icons */
.backlink {
    font-size: 0 !important;
}
    
.backlink:after {
    content: url(data:image/png;base64,) !important;
    margin-right: 4px !important;
}

a.backlink,  
a.backlink:hover {
    border: none !important;
}
  
.backlink:hover::after, 
.backlink.filtered:hover::after {
    content: url(data:image/png;base64,) !important;
}
  
.backlink.filtered:after {
    opacity: 0.6 !important;
}
    
.post.op .backlink:first-child {
    margin-left: 0 !important;
} 

/* Code blocks */
.prettyprint {
    display: inline-block !important;
    background: #f9fafa !important;
    border: 1px solid #e3e7e8 !important;
    border-radius: 2px !important;
    font-size: 12px !important;
}

/* Remove syntax highlighting because it's pretty bad */
.prettyprint span {
    color: #3d4b4f !important;
}

/* Stubs of filtered posts */
.stub {
    line-height: 21px !important;
    font-size: 12px !important;
    padding: 2px 0 2px 0 !important;
}

.stub .show-thread-button {
    margin-left: 88px !important;
}

.stub .menu-button {
    margin: 0 !important;
    margin-right: -2px !important;
    margin-top: -6px !important;
}

.stub .show-thread-button,
.stub .show-reply-button {
    color: #99a6aa !important;
    border-bottom: 1px solid transparent;
    transition: all 0.2s ease 0s;
}

.stub .show-thread-button:hover,
.stub .show-reply-button:hover {
    color: #326085 !important;
    border-bottom: 1px solid #4685b8 !important;
    background: transparent !important;
}

/* Post colors
============== */
.postMessage {
    color: #3d4b4f !important;
}

.postNum a {
    color: #7c878a !important;
    border-bottom: 1px solid transparent !important;
    transition: all 0.2s ease 0s !important;
}

.quote {
    color: #389c6a !important;
}

.name,
.postertrip {
    color: #4f5d61 !important;
}

.nameBlock.capcodeAdmin .name,
.capcode.hand.id_admin {
    color: #ce5f80 !important;
}

.nameBlock.capcodeMod .name,
.capcode.hand.id_mod {
    color: #5f7dce !important;
}

.dateTime,
.fileText,
.posteruid {
    color: #99a6aa !important;
}

.posteruid .hand {
    border-bottom: 1px solid transparent;
    transition: all 0.2s ease 0s;
}

.subject {
    color: #293336 !important;
}

.warning {
    color: #ce6a5f !important;
}

a.useremail {
    text-decoration: none !important;
}

.useremail .name, 
.useremail .postertrip {
    color: #2f6e82 !important;
    border-bottom: 1px solid transparent !important;
    transition: all 0.2s ease 0s !important;
}

.useremail .name:hover, 
.useremail .postertrip:hover {
    color: #5fb3ce !important;
    border-color: #5f9cce !important;
    transition: all 0.2s ease 0s !important;
}

/* Sage posts */
a.useremail[href*='sage'] .name,
a.useremail[href*='Sage'] .name,
a.useremail[href*='SAGE'] .name,
a.useremail[href*='sage'] .postertrip,
a.useremail[href*='Sage'] .postertrip,
a.useremail[href*='SAGE'] .postertrip {
    color: #ce6a5f !important;
    transition: all 0.2s ease 0s;
} 

a.useremail[href*='sage'] .name:hover,
a.useremail[href*='Sage'] .name:hover,
a.useremail[href*='SAGE'] .name:hover,
a.useremail[href*='sage'] .postertrip:hover,
a.useremail[href*='Sage'] .postertrip:hover,
a.useremail[href*='SAGE'] .postertrip:hover {
    color: #99525d !important;
    border-color: #cc4d51 !important;
    transition: all 0.2s ease 0s;
}

/* Links */
.quotelink,
.linkify,
.postMessage a,
.fileInfo a,
.embedder,
#menu a.entry,
#menu .entry a,
#qr #qr-filerm,
#qr #dump-button,
#globalMessage a,
.abbr a {
    color: #5f9cce !important;
    text-decoration: none !important;
