css:
/*=================================================
  musicbird for comunity fm timetable ja jp liquid compact
  url('http://musicbird.jp/cfm/')
 ==================================================*/
@-moz-document url("http://musicbird.jp/cfm/"){

/*--- unwanteds ---*/


/*--- page containers ---*/
body {font-family: inherit !important; font-size: inherit !important;}
#wrap {background: none !important; width: auto !important;}

/*--- header ---*/
#header {padding: 0 !important;}
#header ul.header li {padding: 0 !important;}
#header a img {height: 3em !important; width: auto !important;}
#globalNav
 {background: none !important; width: auto !important; height: auto !important;}
#globalNav ul {height: auto !important;}
#globalNav li {height: auto !important;}
#globalNav a {
 box-sizing: border-box;
 width: auto !important; height: auto !important;
 border: 2pt solid gray; border-radius: 4px;
 margin: 0 0.2em !important; padding: 2pt !important;
 background: black !important;
 }
#globalNav span {font-size: 1em !important; visibility: visible !important;}

/*--- footer ---*/
#footer {width: auto !important; padding: 0 !important;}

/*--- contents ---*/
#content {width: auto !important; margin: 0 !important; padding: 0 !important;}

/*--- 2columns layout ---*/
#main {width: 100% !important; margin: 0 -7em 0 0 !important;}
#main>* {width: auto !important; margin: 0 7em 0 0 !important;}
#sidebar
 {box-sizing: border-box; width: 7em !important; padding: 0 0 0 2pt !important;}

/*--- time table adjustment (TODO) ---*/
}
