@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  url-prefix(http://www.google),
                url-prefix(https://www.google)  

{
/*ETIQUETAS GENERALES*/

body{background: linear-gradient(to bottom, #ffffff 0%,#e5e5e5 100%) !important;}
A:link {text-decoration:none !important ;color:#007dea !important;} 
A:visited {text-decoration:none !important ;color:#007dea !important;} 
A:active {text-decoration:none !important ;color:#007dea !important;} 
A:hover {text-decoration:underline !important ;color:#00b7ea !important;} 

#res {background: linear-gradient(to bottom, #ffffff 0%,#e5e5e5 100%) !important;}
#res cite, div{color:#725E48 !important;}
#res span{color:#725E48 !important;}
#res A:link {text-decoration:none !important ;color:#1A1A1A !important;} 
#res A:visited {text-decoration:none !important ;color:#934C00 !important;} 
#res A:active {text-decoration:none !important ;color:#1A1A1A !important;} 
#res A:hover {text-decoration:underline !important ;color:#934C00 !important;} 

/*INICIO GOOGLE ANTES DE BUSQUEDA*/
/*Encabezado Inicio*/
#gb{background: linear-gradient(to bottom,  rgba(0,183,234,1) 0%,rgba(0,158,195,1) 100%); color: #454545 !important;}   
    /*LINKS DEL #gb*/
    #gb A:link {text-decoration:none !important ;color:#FFFFFF !important;} 
    #gb A:visited {text-decoration:none !important ;color:#D6D6D6 !important;} 
    #gb A:active {text-decoration:none !important ;color:#FFFFFF !important;} 
    #gb A:hover {text-decoration:underline !important ;color:#BFFCFF !important;}
    /*MENU DE APLICACIONES*/
    /*CLASE BOTON INICIAR SESION*/
    #gb_70{background: linear-gradient(to bottom,  #cc0000 0%,#cc0000 100%) !important;}
    /*CAMBIAR COLOR FUENTE BOTON INICIO SESION*/
    #gb a.gb_I.gb_I {color: #FFFFFF !important;}
    
/*BOTONES DE BUSQUEDA*/
/*QUITAR IMAGEN FONDO BOTONES*/
.gbqfba{background:none !important; }
    /*COLOR DE FONDO BOTONES*/
    .gbqfba, .gbqfbb {border: 1px solid rgba(0, 0, 0, 0.1); font-size: 11px; background-color:#FFB900 !important;}
    /*COLOR DE FUENTE BOTONES*/
    #gbqfsa{color: #454545 !important;}
    #gbqfsb{color: #454545 !important;}
    
/*MENU DE APLICACIONES*/
li.gb_b span{color: Black !important;}
#gbwa a:link{color:#007dea !important;}
#gbwa a:visited{color:#007dea !important;}
#gbwa a:active{color:#007dea !important;}
#gbwa a:hover{color:#00b7ea !important;}

/*boton siguiente en la paginacion*/
#navcnt span{color:#007dea !important;} 

/*Pie PÃ¡gina Inicio*/
.fbar{background: linear-gradient(to bottom,  #1A1A1A 0%,#3A3A3A 100%); color: #585858 !important; border: none;}
.fbar A:link {text-decoration:none !important ;color:#cccccc !important;} 
.fbar A:visited {text-decoration:none !important ;color:#abbd73 !important;} 
.fbar A:active {text-decoration:none !important ;color:#dbf043 !important;} 
.fbar A:hover {text-decoration:underline !important ;color:#FFFFFF !important;} 


/*ENCABEZADOS AL BUSCAR EN GOOGLE*/
.gb_nb{background: linear-gradient(to bottom,  rgba(0,183,234,1) 0%,rgba(0,158,195,1) 100%) !important; } /*PRIMER encabezado con busqueda*/
.gb_jb{background: linear-gradient(to bottom,  rgba(0,183,234,1) 0%,rgba(0,158,195,1) 100%) !important; } /*PRIMER encabezado con busqueda*/
.gb_zb{background: linear-gradient(to bottom,  rgba(0,183,234,1) 0%,rgba(0,158,195,1) 100%) !important; } /*PRIMER encabezado con busqueda*/

    #hdtbSum{background: linear-gradient(to bottom, #ffffff 0%,#e5e5e5 100%) !important; border-bottom: 1px solid #e5e5e5;} /*SEGUNDO encabezado con busqueda*/
#topabar{background-color: #cc0000 !important;} /*TERCER encabezado con busqueda*/
    /*Resultado dentro de #topbar*/
    #resultStats{color: White !important;} 

/*BLOQUE DE INFORMACION ENCABEZADO DE UNA BUSQUEDA*/
#tads{background-color: #52B043 !important; box-shadow: 5px 5px 5px #aaa;}
#tvcap span, div, cite{color:White;}
#tvcap A:link {text-decoration:none !important ;color:#B2FFB3 !important;} 
#tvcap A:visited {text-decoration:none !important ;color:#B2FFB3 !important;} 
#tvcap A:hover {text-decoration:none !important ;color:Yellow !important;}


/*BLOQUE DE INFORMACION DERECHO DE UNA BUSQUEDA*/
#rhs_block{background-color: #593EA7; box-shadow: 5px 5px 5px #aaa;}
#rhs_block div, span, cite{color:White !important;}
#rhs_block a:link{color:#DAB8FC !important;}
#rhs_block a:visited{color:#DAB8FC !important;}
.kp-blk{background-color: #593EA7;}
.kno-msc-ec:hover {background: none repeat scroll 0% 0% #733ea7; cursor: pointer;}
#rhs_block table{background-color: #593EA7;}

#nycp{background-color: Silver !important; border: 1px solid White;}/*Informacion de la empresa*/

}
