css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("volafile.io") {



/*

>Based upon the original .css of GNUsucks

>Modified by 4age

>fuckin rgb how do you people deal with it

*/







* {

   border: 0px !important;

}*





mark {

    background-color: #1b1b1b!important;

    color: rgb(114,100,172)!important;

}



textarea,input[type=text],input[type=password] {

    background-color: #1b1b1b !important;

    color: #ffffff !important;

}



textarea.defaultValue,input[type=text].defaultValue,input[type=password].defaultValue {

    color: #ffffff!important;

}



.button,input[type=submit] {

    background-color: /*[[cl]]*/ !important;
    color: #1b1b1b!important;

}



.button:hover,input[type=submit]:hover {

    background-color: /*[[cl]]*/ !important;

}



.button:active,input[type=submit]:active {

background-color:#e8b0ff!important;

}



.button.green,input[type=submit].green {

    background-color: rgb(114,100,172)!important;

}



.button.green:hover,input[type=submit].green:hover {

background-color:#30d376!important;

}



.button.green:active,input[type=submit].green:active {

background-color:#45d784!important;

}



.toggle i {

    color: rgb(89,89,89)!important;

}



.toggle.enabled i {

    color: /*[[cl]]*/ !important;

}



.nano>.pane>.slider {

    background: rgb(59,59,59)!important;

}

.nano>.pane>.slider:hover {

    background: rgb(59,59,59)!important;

}



html {

    background-color: #1b1b1b !important;

}



body {

    color: #a5a5a5 !important;

}



html.index h1.logo {

    color:/*[[cl]]*/ !important;

}



html.index h1.logo span {

    color:/*[[cl]]*/ !important;

}



html.room #header {

    background-color: #1b1b1b!important;

}



html.room #header #header_row_container a i.green-icon {

    color: /*[[cl]]*/ !important;



}



html.room #header #header_row_container a:hover i.green-icon {

    color:/*[[cl]]*/ !important;



}



html.room #header #header_row_container a:active i.green-icon {

    color: rgb(76,186,115)!important;



}



html.room #header #room_search #clearsearch {

    color: rgb(119,119,119)!important;



}



html.room #chat_frame #chat_messages_frame #chat_messages .message.highlight {

    background-color: rgb(31,31,31)!important;

}



html.room #chat_frame #chat_hbar {

    background-color: rgb(31,31,31)!important;

}



Html.room #file_list .file.failed {

background-color:#8E4040!important;

}



.file_tag.tag_key_user {

    background-color: rgb(44,44,44)!important;

    border-color: rgb(99,99,99)!important;

    color:rgb(139,139,139)!important;

}



.file_tag {

    background-color: rgb(31,31,31)!important;

    border-color: rgb(99,99,99)!important;

    color:rgb(139,139,139)!important;

}



.file_tag:hover {

    background-color: rgb(20,20,20)!important;

    color: /*[[cl]]*/ !important;

}



html.room #overlay {

    background-color: #1b1b1b!important;

    color: rgb(89,89,89)!important;

}



div.ui_frame {

    background-color: #1b1b1b!important;

}



a,a:visited,html.room #file_list .file {

    color: rgb(119,119,119)!important;

}



a:hover,html.room #header #room_search #clearsearch:hover,html.room #file_list .file .fcontrol i.clickable:hover {

    color:/*[[cl]]*/ !important;

}



a:active,html.room #header #room_search #clearsearch:active,html.room #file_list .file .fcontrol i.clickable:active {

    color:/*[[cl]]*/ !important;

}



html.room #file_list .file.playing i.filetype,html.room #file_list .file.loading i.filetype{

    color:/*[[cl]]*/ !important;

}

.message.admin b {
    color:/*[[cl]]*/ !important;}
    
.message.admin.profile {
    border-left: solid 4px /*[[cl]]*/  !important;}


html.room #file_list.even .file:nth-child(2n),html.room #file_list.uneven .file:nth-child(2n+1) {

    background-color: rgb(31,31,31)!important;

}



.highlight b{

    color: rgb(144,100,172)!important;

}  



* {

    border-color:rgb(49,49,49)!important;

}



#room_name_container {

    color:/*[[cl]]*/ !important;

    text-align:center!important;

}




.fcontrol, .fsize, .fstatus,.toggle i,.fcontrol,I.fa.fa-download.clickable{

    color:rgb(60,60,60)!important;

}

.fcontrol i.clickable{

    color:rgb(69,69,69)!important;

}



.file a.fname:hover{

    color: /*[[cl]]*/ !important;

}  



html.room #file_list  .file.uploading ,html.room #file_list.uneven .file.uploading:nth-child(2n+1),html.room #file_list.even .file.uploading:nth-child(2n)  {

    background-color: rgb(15,15,15)!important;

}

html.room #file_list  .file.uploading .fstatus, #chat_messages .message.user b  {

    color: /*[[cl]]*/ !important;

}







}
