css:
/*========================================================
  hatena::diary liquid compact 2014/02
  domain(d.hatena.ne.jp)
  ========================================================*/
@-moz-document domain('d.hatena.ne.jp') {
/*--- unwanteds ---*/
.ad,
dummy#dummy.dummy {display: none !important;}

/*--- containers ---*/
html {font-family: inherit !important;}
body {background-size: 100% auto !important; width: auto !important;}
body div {min-width: auto !important; max-width: inherit !important;}
.hatena-body {width: auto !important; margin: 0 !important; padding: 0 !important;}
#wrap {width: auto !important;}
.hatena-body div {background-size: 100% auto !important;}

/*--- global headers ---*/
#simple-header {width: auto !important; margin: 0 !important; padding: 0 !important;}
#simple-header ul {position: static !important; display: inline !important;}

/*--- page headers ---*/
body>h1 {width: auto !important; margin: 0 !important; padding: 0 !important;}

/*--- 2columns topics type1 ---*/
#main {
 position: relative !important; float: left;
 box-sizing: border-box;
 width: 100% !important; margin: 0 -180pt 0 0 !important;
 padding: 0.5em !important;
 }
#main>* {
 box-sizing: border-box;
 width: auto !important; margin: 0 180pt 0 0 !important;
 }
#sidebar {
 position: relative !important; float: right;
 box-sizing: border-box; width: 180pt !important; margin: 0 !important;}
#sidebar * {word-wrap: break-word !important;}
#sidebar li {list-style: disc !important;}

/*--- 2columns topics type2 ---*/
.hatena-body>.main {
 position: relative !important; float: left;
 box-sizing: border-box;
 width: 100% !important; margin: 0 -180pt 0 0 !important;
 padding: 0.5em !important;
 }
.hatena-body>.main>* {
 box-sizing: border-box;
 width: auto !important; margin: 0 180pt 0 0 !important;
 }
.hatena-body>.main>* div {width: auto !important; margin: 0 !important;}
.hatena-body>.main>* img {max-width: 100% !important;}

.hatena-body>.sidebar {
 position: relative !important; float: right;
 box-sizing: border-box; width: 180pt !important; margin: 0 !important;
 }
.hatena-body>.sidebar * {word-wrap: break-word !important;}
.hatena-body>.sidebar li {list-style: disc !important;}
}
