css:
@-moz-document domain("answers.yahoo.com") {


html
{
  background-color: #222 !important;
}

body
{
  color: #eee; 
}

a
{
  color: #C09070; 
}

a:visited
{
  color: #B76027;
}



.unselected
{
  color: #B76027; 
}

.selected
{
  color: #C09070; 
}



.ya-page #yucs, .ya-page #ya-body-container, #yucsHead
{
  background: #ccc !important;
}

#yucs .yucs-fc
{
  text-shadow: none;
}



#ya-c
{
  background-color: #444;
  color: #eee;
}

.qa-container .content, .answer-rating .desc, .reference, #yan-question .q-details, #ya-ba .ba-source, #ya-ba h3.ba-rating
{
  color: #eee; 
}

.q-follow
{
  color: #111;
}

.yshortcuts
{
  color: #B7C7FF !important;
}



.yom-mod
{
  background-color: #272727 !important;
}

.yom-hd
{
  color: #ddd; 
}

.yom-bd
{
  color: #eee; 
}




.yui3-x-carousel-content, .question-feed li, .question-feed li div
{
  background: #444; 
}

.question-feed li
{
  color: #eee; 
}

.question-title a
{
  color: #C09070 !important; 
}

.question-meta a
{
  color: #B7C7FF !important; 
}
}
