    }
    input#email.inputtext, input#pass.inputtext {
        border-color:darkred !important;
        font-family:"Consolas", "Verdana", monospace !important;
    }
    label[for="email"], label[for="pass"],
    label[for="persist_box"]
    {
        color:white !important;
    }
    label#loginbutton.uiButtonConfirm {
        background-image:none !important;
        background-color:darkred !important;
        border:2px solid !important;
        border-radius:4px !important;
        border-color:rgb(200, 0, 0) !important;
        box-shadow:none !important;
    }
    div.gradient {
        background:white !important;
    }
    div._5iyx {
        color:rgb(200, 0, 0) !important;
    }
