css:
@-moz-document domain("rateyourmusic.com") {
body {
  background-color: #444 !important;
}

#navtop li {
  background: #f7f7f7;  
}

.submenu_ext
{
  background: #333; 
}

#content {
  background-color: #222 !important;
}

h1, h2, h3, h4, h5
{
  color: #DD9966 !important;
}

a, a.genre, a.artist, a.album {
  color: #C08030 !important;
}

.number
{
  color: #999 !important;
}

.user
{
  color: #AA5533 !important;
}

.usero
{
  color: #DD7755 !important;
}

.useri
{
  color: #AA5533 !important;
}

.profileb {
  background-color: #ccc !important;
}

div.profilehii {
  color: #ccc !important;  
}

.albumoverview:tbody:tr:td {
  background-color: #444 !important;
}

div.bubble_header {
  background: #606060 !important;
}

div.bubble_content {
  background: #444 !important;
  color: #ccc;
}

div.release_page {
  background-color: #222 !important;
}

div.review_header {
  background: #ccc !important;
}

div.review_body {
  background: #ddd !important;
}

.section_reviews.section_outer, .section_my_catalog.section_outer, .section_main_info.section_outer, .section_catalog.section_outer, .section_issues.section_outer {
  background: #444 !important;
}

.review_sort {
  color: #d0d0d0 !important;
}

.mbgen tr:nth-child(odd)
{
  color: #ddd !important;
  background: #404040 !important;
}

.mbgen tr:nth-child(even)
{
  color: #ddd !important;
  background: #373737 !important;
}

.mbgen tr:nth-child(odd) td blockquote, .mbgen tr:nth-child(even) td blockquote
{
  color: #ddd !important; 
}

.or_q_ownership
{
  color: #ccc !important; 
}

.or_q_rating_date_s
{
  color: #a0a0a0 !important; 
}

.smallgray, a.smallgray
{
  color: #bbb !important;
}

a.smallbutton
{
  background-color: #3A3732;
  border-right: 1px solid #555;
}




/* Album Page */

.release_page_header {
  color: #DD9966 !important;
}

.release_left_column {
  background: #222 !important;
}

.release_left_column > div {
  background: #222 !important;
}

.num_ratings
{
  color: #777;
}

div.review_body
{
  background: #333 !important;
}

.review_body span
{
  color: #c0c0c0 !important;
}

ul.credits, ul.lists, ul.lists li
{
  background: #333;
}

.role_name
{
  color: #a0a0a0; 
}

div.comments, .comments_post
{
  background: #333;
}

ul.tracks li:nth-child(odd)
{
  background: #404040 !important;
}

ul.tracks li:nth-child(even)
{
  background: #373737 !important;
}

ul.tracks li.track
{
  color: #ddd;
}

div.release_navigation_links
{
  background: #eee; 
}

ul.suggestions, ul.suggestions li
{
  background: #333;
  /* background: #222; */
  color: #909090;
}

/* Friend */
.catalog_header.friend, .review_header.friend {
  background: #152D15 !important;
}




/* Artist Page */

.row
{
  background: #222 !important;
}

span.contributors a
{
  color: #a0a0a0 !important;
}
  
.artist_left_col
{
  background: #444;
}

.rough_guide_text
{
  background: #222 !important;
  color: #909090 !important;
}

.comment_body
{
  color: #909090;
}

.rough_guide_text .comment_header
{
  background: #444; 
}

.section_artist_biography
{
  background: #222;
  color: #909090;
}

.artist_page .section_lists, .artist_page .section_discussion
{
  background: #222;
  color: #909090;
}

.artist_page .section_discussion
{
  color: #909090; 
}

.artist_page_section_button_film, .artist_page_section_button_music
{
  color: #997744 !important;
  border-bottom: 2px #776655 solid;
}

.artist_page_section_left
{
  border-right: 1px solid #666;
}

ul#discussion, ul#discussion li
{
  background: #333;
}

.section_artist_info
{
  color: #888;
}

.disco_header_section
{
  border-bottom: 1px #666 solid;
}

.disco_header
{
  color: #909090;
}

.disco_release:hover
{
  background: #555;
}

.disco_avg_rating
{
  color: #aaa;
}

.disco_year_ym, .disco_year_ymd
{
  color: #888;
}




/* List Page */
.trodd
{
  color: #ddd !important;
  background: #404040 !important;
}

.treven
{
  color: #ddd !important;
  background: #373737 !important; 
}




/* Search */
.infobox
{
  background: #333 !important; 
}

.infobox:hover
{
  background: #555 !important;
}




/* Wiki */

div#wiki_commentpage .commentbody
{
  background-color: #444 !important;
  color: #ddd !important;
}

div#wiki_commentpage .commentheadera {
   background-color: #BBB !important;
}
}
